[[module CSS]]
#page-title {
  display: none;
}

.meta-title {
  border-bottom: solid 1px #bbbbbb;
  color: #990011;
  font-weight: normal;
  margin: 0 0 0.6em;
  padding: 0 0 0.25em;
  font-size: 200%;
}

.meta-title p {
  margin: 0;
}
#u-ratingboxhid {   
 opacity:0.05;  transition: all 0.5s ease-out; 
}
#u-ratingboxhid:hover {  
 opacity:1;
}

[[/module]]
[[module ListPages category="fragment" parent="." limit="1" order="name" offset="@URL|0"]]
[[div class="meta-title"]]
%%title%%
[[/div]]
[[/module]]
[[>]]
[[span id="ratingboxhid"]]
[[module Rate]]
[[/span]]
[[/>]]
[[module ListPages category="fragment" parent="." limit="1" order="name" offset="@URL|0"]]

%%content%%
[[/module]]
