[[module css]]
.page {
    display: block;
    overflow: hidden;
    font-family: "Monotype Corsiva", "Bradley Hand ITC", sans-serif;
    font-style: normal;

    background-attachment: scroll;
    background-clip: border-box;
    background-color: transparent;
    background-image: linear-gradient(to top ,rgb(202, 219, 228) 0%, rgb(231, 233, 220) 8%);
    background-origin: padding-box;
    background-position: 0px 8px;
    background-repeat: repeat;
    background-size: 100% 20px;

    border: 1px solid #CCC;
    border-radius: 10px;
    padding: 10px 10px;
    margin-bottom: 10px;

    box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.2)
    }
.page p,
.page ul {
    line-height: 20px;
    margin: 0;
}

/*什么嘛？我还挺绿的嘛*/
div#container-wrap {
    background: url(http://scp-wiki-cn.wikidot.com/local--files/scp-cn-1410/%E8%BF%99%E5%AD%A4%E7%8B%AC%E7%9A%84%E7%BB%BF.png);
    background-repeat: repeat-x;
}
/*侧栏部分字体绿色1*/
#side-bar a:visited {
    color: rgb(0,159,107);
}
/*也许还能更绿*/
a {
    color: rgb(0,159,107);
    text-decoration: none;
    background: transparent;
}
/*小杠杠也要绿*/
#side-bar .heading {
    color: #600;
    border-bottom: solid 1px rgb(0,159,107) !important;
    padding-left: 15px;
    margin-bottom: 5px;
    font-size: 8pt;
    font-weight: bold;
}
/*评分之绿（左）*/
.page-rate-widget-box .rate-points {
    background-color: rgb(0,159,107) !important;
    border: solid 1px rgb(0,159,107);
        border-right-color: rgb(0, 159, 107);
        border-right-style: solid;
        border-right-width: 1px;
    border-right: 0;
    border-radius: 5px 0 0 5px;
}
/*评分之绿（中）*/
.page-rate-widget-box .rateup a, .page-rate-widget-box .ratedown a {
    background: transparent;
    color: rgb(0,159,107);
    padding: 0 4px;
    margin: 0 1px;
}
/*取消评分部分*/
.page-rate-widget-box .cancel a {
    background: transparent;
    text-transform: uppercase;
    color:#fff;
}
/*点击取消评分*/
.page-rate-widget-box .cancel a:hover {
    border-radius: 0 3px 3px 0;
    background: transparent;
    color: #fffff0;
    text-decoration: none;
}
/*评分右侧颜色更改*/
.page-rate-widget-box .cancel {
    background-color: rgb(0,159,107);
    border: solid 1px rgb(0,159,107);
        border-left-color: rgb(0, 159, 107);
        border-left-style: solid;
        border-left-width: 1px;
    border-left: 0;
    border-radius: 0 5px 5px 0;
}
/*点击评分*/
.page-rate-widget-box .rateup a:hover, .page-rate-widget-box .ratedown a:hover {
    background: rgb(0,159,107);
    color: #fffff0;
    text-decoration: none;
}
/*中部评分模块上下边框*/
.page-rate-widget-box .rateup, .page-rate-widget-box .ratedown {
    background-color: #fff6f0;
    border-top: solid 1px rgb(0,159,107);
    border-bottom: solid 1px rgb(0,159,107);
    font-weight: bold;
}
/*侧栏边框部分*/
#side-bar .side-block {
    padding: 10px;
    border: 1px solid rgb(0,158,107);
    border-radius: 10px;
    box-shadow: 0 2px 6px rgba(0, 159, 107, 0.15);
    background: #fff;
    margin-bottom: 15px;
}
/*侧栏的小线线*/
#side-bar .heading {
    color: rgb(0,159,107) !important;
    font-size: 8pt;
    font-weight: bold;
}
hr {
    margin: 1em 2em;
    padding: 0;
    height: 1px;
    border: none;
    color: rgb(0,159,107) !important;
    background-color: rgb(0,159,107);
}
/*侧栏小字*/
#side-bar div.menu-item .sub-text {
    font-size: 80%;
    color: rgb(0,159,107);
}
/*底栏小小字*/
#page-info {
    clear: both;
    text-align: right;
    font-size: 87%;
    margin: 2px 2px;
    color: rgb(0,159,107);
}
/*页注*/
#footer a {
    color: rgb(0,159,107);
    background: transparent;
}
#footer {
    clear: both;
    font-size: 77%;
    background: #444;
    color: rgb(0,159,107);
    margin-top: 15px;
    padding: 3px 10px;
}
#license-area {
    padding: 0.5em 0;
    color: rgb(0,159,107);
    text-align: center;
}
/*顶栏点击绿绿*/
#top-bar ul li.sfhover a, #top-bar ul li:hover a {
    background: #eee;
    color: rgb(0,159,107);
    border-left: solid 1px rgba(64,64,64,1);
    border-right: solid 1px rgba(64,64,64,1);
}
/*顶部搜索栏字体颜色*/
#search-top-box input.empty {
    color: #fff;
}
/*顶部搜索栏的框内背景*/
#search-top-box-input {
    border: solid 1px rgba(0, 159, 107, 0.15);
    border-radius: 5px;
    background-color: rgb(0,159,107);
    box-shadow: inset 1px 1px 3px rgba(0, 159, 107, 0.15);
}
/*讨论区小字*/
a:visited {
    color: rgb(0,159,107);
}
/*点击搜索栏*/
#search-top-box-input:hover, #search-top-box-input:focus {
    border: solid 1px #fff;
    color: #fff;
    background-color: rgba(0, 159, 107, 0.15);
    box-shadow: inset 1px 1px 3px #fff;
}
/*搜索按钮*/
#search-top-box-form input[type="submit"] {
    border: solid 1px #fff;
    border-radius: 5px;
    padding: 2px 5px;
    font-size: 90%;
    font-weight: bold;
    color: #fff;
    background: linear-gradient(to bottom, green,rgb(0, 159, 107),rgba(0, 159, 107, 0.15));
    box-shadow: 0 1px 3px rgba(0,0,0,.5);
    cursor: pointer;
}
/*点击搜索按钮*/
#search-top-box-form input[type="submit"]:hover, #search-top-box-form input[type="submit"]:focus {
    border: solid 1px #fff;
    color: #fff;
    text-shadow: 0 0 1px rgba(255,255,255,.25);
    background-color: #966;
    background: linear-gradient(to bottom, rgba(0, 159, 107, 0.15),rgb(0, 159, 107),green);
    box-shadow: 0 1px 3px rgba(0, 159, 107, 0.15);
}
/*账户字体颜色*/
#login-status ul a {
    color: rgb(0,159,107);
}
/*点击后颜色*/
#account-options li a:hover {
    color: rgba(0,159,107,0.15);
}
/*我的账户*/
#login-status a {
    background: transparent;
    color: rgb(0,159,107);
}
/*天啊我自己也绿了*/
#login-status {
    color: rgb(0,159,107);
    font-size: 90%;
}

