.hover {
    color: #000;
}
.hover:hover {
    text-decoration: none;
    border-bottom: none;
    color: #000;
}

.hover span { display: none;}
.hover:hover span {
    position: absolute;
    display: inline;
    margin: 0px 0px;
    height: auto;
    width: 150px;
    background: #fff;
    border: 2px solid #000;
    color: #000;
    padding: 1em;
    text-align: center;
}
.hover:hover span span {
    position: relative;
    left: -200px;
    margin: auto;
    height: auto;
    border: none;
    padding: 0;    
}
[[/module]]

