[[module css]]

body,#main-content,#container{
background:#000;
color:white;
}

a {
    color: red;
    color: red;
}
a:visited {
    color: red;
    color: red;
}

#footer{
opacity:0;
}

.footnote{
background:black;
}

#page-options-container{
font-family:'华文新魏';
}

div#container-wrap {
background: #fff;
background-color: #fff;
}


.meta-title {
display:none
}


.page-tags,.page-rate-widget-box,#search-top-box,#top-bar,#side-bar,#login-status,#page-title,#header,#footer,#license-area,#footer,#license-area  {
display:none
}

#page-content {
    min-height: 720px;
}

#main-content {
    margin:0 5% 0  5%;
}

.page-rate-widget-box {
    display: inline-block;
    border-radius: 0px !important;
    box-shadow: 0px 0px 0px rgba(0, 0, 0, .5);
margin-top: -10px;
    margin-bottom: 10px;
    margin-right: 0em;
text-transform:uppercase;
}

div.page-rate-widget-box .rate-points {
    background-color: transparent;
    border: solid 0px #000;
    border-bottom: solid 2px #000;
    border-right: 0;
    border-radius: 0px;
    color: white;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: transparent !important;
border: 0px;
    border-bottom: solid 1.5px red;
    font-weight: 900;
    font-size: 1.2em;
    margin-left: 5px;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background-color: transparent;
    color: red;
    padding: 0 4px;
    margin: 0 1px;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background-color: #6c1616;
    color: white;
    text-decoration: none;
}

.page-rate-widget-box .cancel {
    background-color: transparent;
border: 0px;
    border-bottom: solid 2px #000;
border-radius: 0px;
    font-weight: 900;
    font-size: 1.2em;
    margin-left: 5px;
}

.page-rate-widget-box .cancel a {
    background: transparent;
    text-transform: uppercase;
    color: #000;
}

.page-rate-widget-box .cancel a:hover {
    background-color: #6c1616;
    color: #fff;
    text-decoration: none;
}
[[/module]]
