function popup(color, title, content, hasinputbox, submitevent) {
    saveState = document.getElementById('layer').innerHTML;
    var popupCont = "<div class=\"divider popup\" id=\"popup\" style=\"border-color:" + color + "; color: " + color + "\"><strong>" + title + "</strong><br><br>" + content + "<br><br>";

