.page-rate-widget-box .cancel a {
  background: transparent;
  text-transform: uppercase;
  color: #0d3754;
}
.page-rate-widget-box .cancel a:hover {
  border-radius: 0 3px 3px 0;
  background: #0d3754 ;
  color: #5eb8f2;
  text-decoration: none;
