[[module css]]
.image-block .image-caption-top {
    background: var(--image-block-caption-background);
    padding: 2px 0px;
    font-size: 80%;
    font-weight: bold;
    text-align: center;
    width: 300px;
    color: #BFBFBF;
}

.image-caption-top img {
    filter: invert(91%);
    opacity: 0.8;
    margin-top: 1px;
    width: 35px;
}

.hazardBlock {
    position: relative;
    display: inline-block;
}

.hazardBlock .hazardText {
    visibility: hidden;
    width: 120px;
    background-color: #333;
    color: #fff;
    text-align: center;
    padding: 5px 0;
    border-radius: 6px;
    width: 120px;
    bottom: 100%;
    left: 50%;
    margin-left: -60px;
    position: absolute;
    z-index: 1;
}

.hazardBlock:hover .hazardText {
    visibility: visible;
}

.hazardBlock .hazardText::after {
    content: " ";
    position: absolute;
    top: 100%;
    left: 50%;
    margin-left: -5px;
    border-width: 5px;
    border-style: solid;
    border-color: #333 transparent transparent transparent;
}

.hazardBlock .hazardText {
    opacity: 0;
    transition: all 0.2s ease-in-out;
}

.hazardBlock:hover .hazardText {
    opacity: 1;
}

.hazardTable {
    padding: 0 5px;
}

@media (max-width: 768px) {
    .hazard-image-block {
        width: 99%!important;
    }

    .hazardTable {
    padding: 0 15px;
    }
}

.propertiesText,
.hazardTableText {
width: 4rem !important;
}

table.hazardTable {
    padding: 0 !important;
}
