[[module css]]

div#page-content *,
div#page-content h1,
div#page-content h2,
div#page-content h3 {
    font-family: inherit;
}

div.blockquote.content {
    border: solid 1px transparent;
    background-color: #EEE;
    border-radius: 1rem;
    margin-left: 0;
    margin-right: 0;
}

hr {
    margin-left: 0;
    margin-right: 0;
}

span.black {
    background-color: #000;
    cursor-events: none;
}

div.scp-image-block div.scp-image-caption,
div.scp-image-block img {
    width: auto !important;
}

div#container-wrap {
    background-image: url(http://scpsandboxcn.wdfiles.com/local--files/mscarlet/body_bga.png);
    background-repeat: repeat-x;
}
#header {
    background-image: url(http://scpsandboxcn.wdfiles.com/local--files/mscarlet/logo-af3.png);
    background-repeat: no-repeat;
}

#header h1 a {
    display: block;
    margin: 0;
    padding: 80px 0 25px;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-size: 120%;
    text-shadow: none;
    text-decoration: none;
}

#header h1 a::before {
    content: "Ocean Park Hong Shing ";
    color: #FFFFFF;
    background: transparent;
    font-family: BauhausLTDemi, Arial, sans-serif;
    font-size: 150%;
    text-decoration: none;
    text-shadow: 3px 3px 5px #000000;
}

#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    text-shadow: none;
}

#header h2 span::before {
    content: 'Seal Cute Pup';
    color: #f0f0c0;
    font-size: 150%;
    text-shadow: 1px 1px 1px #000;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}

h1, #page-title {
    color: #019;
    padding: 0 0 0.25em;
    margin: 0 0 0.6em;
    font-weight: normal;
}

.meta-title {
    border-bottom: solid 1px #bbb;
    color: #019;
    font-weight: normal;
    margin: 0 0 0.6em;
    padding: 0 0 0.25em;
    font-size: 200%;
}

a {
    color: #019;
    text-decoration: none;
    background: transparent;
}
a:visited {
    color: #45d;
}

#side-bar a:visited {
    color: #45d;
}

#side-bar .side-block {
    padding: 10px;
    border: 1px solid #019;
    border-radius: 10px;
    box-shadow: 0 2px 6px rgba(102,0,0,.5);
    background: #fff;
    margin-bottom: 15px;
}
#side-bar .side-area {
    padding: 10px;
}
#side-bar .heading {
    color: #019;
    border-bottom: solid 1px #019;
    padding-left: 15px;
    margin-bottom: 5px;
    font-size: 8pt;
    font-weight: bold;
}

#side-bar div.menu-item .sub-text {
    font-size: 80%;
    color: #019;
}

#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #019;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    margin-top: 10px;
    margin-bottom: 5px;
    font-size: 8pt;
    color: #019;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #45d;
    text-decoration: none;
}

.feature-block {
    height: 500px;
}
.feature-block .panel-body {
    height: 430px;
    overflow-y: hidden;
}
.feature-block .feature-title {
    font-size: 120%;
    font-weight: bold;
}
.feature-block .feature-title img {
    display: block;
    width: 300px;
    border: solid 1px #666;
    margin: 5px auto 0;
    border-radius: 10px;
}
.feature-block .feature-subtitle {
    font-size: 90%;
    text-align: center;
}

.feature-block .feature-title > p,
.feature-block .feature-subtitle > p {
    margin: 0;
}

.news-block .panel-body {
    padding: 0;
}
.news-block .panel-body .alternate {
    padding: 5px 10px;
}
.news-block .news-title {
    font-weight: bold;
    font-size: 110%;
    margin-bottom: 5px;
    color: #633;
}
.news-block .news-content {
    margin: 5px 0;
}
.news-block .news-title > p,
.news-block .news-content > p {
    margin: 0;
}

.content-panel {
  padding: 10px 15px;
}

[[/module]]
