.blinkbar {animation: blink 1.5s infinite; letter-spacing: -0.13em;}
@keyframes blink{to{opacity: .0;}}

.flashing {
  -webkit-animation: flashit 100s infinite;
              animation: flashit 100s infinite;

background:url(http://scp-jp-storage.wikidot.com/local--files/file:5018858-10-3edp/IMG-RedBlood-DARK-pot.png) no-repeat;
   background-size: 95%;

    height: 100%;
    width:110%;
    left:0px;
    bottom:0%;
    padding: 1px 15px;
    z-index: 20;
    transform: rotateY(180deg);
}

@keyframes flashit{
0%   {
         opacity:0;
         display:none;
         z-index:-1;
        }
99.5%  {
opacity:0;
display:none;
z-index:-1;
}
99.96%  {
opacity:0.5;
display:block;
z-index:20;
}
99.97%  {
opacity:1;
display:block;
z-index:20;
position:Fixed ;
}
99.98%  {
opacity:0.1;
display:block;
z-index:20;
position:Fixed ;
}
100%  {
opacity:0;
display:none;
position:relative;
z-index:-1;
}
}

.i-see-you {
  -webkit-animation: darkness 60s;
             animation: darkness 60s;
  -webkit-animation-iteration-count:1;
              animation-iteration-count:1;

 background:url(http://scp-jp-storage.wikidot.com/local--files/file:5018858-11-ul7f/PIC-dark-eye.png) repeat;
    width:100%;
    left:0px;
    bottom:0%;
    padding: 1px 15px;
    height: 100%;
    z-index:19;
    position:Fixed;
}

.moduleinfront {
    width:100%;
    left:0px;
    bottom:0%;
    padding: 1px 15px;
    height: 100%;
    z-index:19;
    position:Fixed;
}


@keyframes darkness{
0% {opacity: 0;}
60% {opacity: 0;filter: brightness(0%);-webkit-filter: brightness(0%);}
97% {opacity: 1;filter: brightness(190%);-webkit-filter: brightness(190%);}
99% {opacity: 1;filter: brightness(1100%);-webkit-filter: brightness(1100%);}
100% {opacity: 1;}
}



