document.getElementById("IPlocation").innerHTML="注意："+"<br />您的地理坐标已经被上传，"+"<br />请等待附近的sdxzdgsrdgvexdf";
  }



        }  
function showError(error)
  {
  switch(error.code)
    {
    case error.PERMISSION_DENIED:
      x.innerHTML="警告，您具体的地理位置无法被基金会定位，请注意可能的swcqwVSCAZ！"
      break;
