[[include :scpsandboxcn:etinjat-info-css]]

[[module css]]

/*-------------------*/
#u-ratingboxhid {   
 opacity:0.1;  transition: all 0.5s ease-out;
}
#u-ratingboxhid:hover {  
 opacity:1;
}

.check-box{
position:Fixed ;
left:20px;
bottom:-30px;
}

.bgr-logo{
background: #D5D5D5;
}

@media (max-width: 479px) {
.check-box{
position:inherit ;
left:20px;
bottom:0%;
}
.phone{
font-size:60%;
}
.bgr-logo{
background: url(http://scp-wiki.wdfiles.com/local--files/djkaktus-s-proposal/amber.png) center no-repeat; 
}
.scp-logo{
display:none;
}

}

.blinkbar {animation: blink 1.5s infinite; letter-spacing: -0.13em;}
@keyframes blink{to{opacity: .0;}}

.flashing {
  -webkit-animation: flashit 100s infinite;
              animation: flashit 100s infinite;

background:url(http://scp-wiki-cn.wikidot.com/local--files/scp-cn-1990/IMG-RedBlood-DARK-pot.png) no-repeat;
   background-size: 95%;

    height: 100%;
    width:110%;
    left:0px;
    bottom:0%;
    padding: 1px 15px;
    z-index: 20;
    transform: rotateY(180deg);
}

@keyframes flashit{
0%   {
         opacity:0;
         display:none;
         z-index:-1;
        }
99.5%  {
opacity:0;
display:none;
z-index:-1;
}
99.96%  {
opacity:0.5;
display:block;
z-index:20;
}
99.97%  {
opacity:1;
display:block;
z-index:20;
position:Fixed ;
}
99.98%  {
opacity:0.1;
display:block;
z-index:20;
position:Fixed ;
}
100%  {
opacity:0;
display:none;
position:relative;
z-index:-1;
}
}

.you-here{
     -webkit-animation: darkness 40s;
             animation: darkness 40s;
  -webkit-animation-iteration-count:1;
       animation-iteration-count:1;
    background-size:contain ;
    background-color: #000;
    background-position:  top;
    width:100%;
    left:0px;
    bottom:0%;
    padding: 1px 15px;
    height: 100%;
    z-index:19;
     position: fixed; 
    filter: blur(0px)
}

.i-see-you {
  -webkit-animation: see 40s;
             animation: see 40s;
  -webkit-animation-iteration-count:1;
              animation-iteration-count:1;
 background:url(http://scpsandboxcn.wikidot.com/local--files/image/PIC-darkeye-2.jpg) repeat;
    background-size:contain ;
    background-color: #000;
    background-position:  top;
    width:100%;
    left:0px;
    bottom:0%;
    padding: 1px 15px;
    height: 100%;
    filter: blur(0px);
    opacity: 0.2;
}

@keyframes darkness{
0% {background:transparent;}
20% {background:transparent;}
90% {background:#000;}
}

@keyframes see{
0% {opacity: 0;filter:blur(50px);-webkit-filter:blur(50px);}
30% {opacity: 0;filter: blur(50px);-webkit-filter: blur(50px);}
40% {opacity: 0.2;filter:  blur(50px);-webkit-filter:blur(50px);}    
60% {opacity: 0.5;filter: blur(50px);-webkit-filter:  blur(50px);}    
70% {opacity: 1;filter:blur(50px);-webkit-filter: blur(50px);}
80% {opacity: 1;filter: blur(0px);-webkit-filter:blur(0px);}
95% {opacity: 1; filter:blur(0px);-webkit-filter:blur(0px);}
100% {opacity: 0.2; filter:blur(0px);-webkit-filter:blur(0px);}
}


[[/module]]


