    border-color: rgba(0,0,0,0.267);
}

/* ---- HEADER ---- */

 div#container-wrap{
     background-image: url('http://scp-wiki.wdfiles.com/local--files/theme%3Asimple-yonder/bg3.png');
     background-repeat: repeat-x;
}

div#header {
    background-image: url('http://scp-wiki.wdfiles.com/local--files/theme%3Asimple-yonder/logo.png');
    background-size: 7.3rem;
    background-position: 3px 29px;
}
@media (max-width: 767px) and (min-width: 480px) {
    div#header {
      background-size: 6rem;
      background-position: -4px 44px;
    }
}
@media (max-width: 479px) {
    div#header {
      background-size: 4.6rem;
      background-position: -3px 58px;
    }
}

#login-status,
#login-status a {
    color: white;
    font-weight: bold;
}
#account-topbutton{
    border: 0px;
}

#search-top-box-input,
#search-top-box-input:hover,
#search-top-box-input:focus,
#search-top-box-form input[type=submit],
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: none;
    background: #282828;
    box-shadow: none;
    border-radius: 2px;
    color: #f4f4f4;
}

#search-top-box input.empty {
    color: #bbbbbb;
}

div#header h1 a span {
    font-family: 'jostregular', '黑体';
    font-weight: bold;
    color: #f4f4f4;
    text-shadow: 0px 0px 0px #000;
}
div#header h2 span {
    font-family: 'Open Sans', sans-serif;
    font-weight: bold;
    color: #f4f4f4;
    padding: 17px 0;
    text-shadow: 0px 0px 0px #000;
    white-space: pre;
}

/* ---- SIDE BAR ---- */

div#side-bar {
    font-size: 104%;
    background-color: white;
}

#side-bar .side-block {
    border: #ffffff 4px;
    border-radius: 0;
    box-shadow: none;
    background: #ffffff !important;
    padding: 2px;
}
#side-bar .heading {
    border: none;
    border-radius: 0;
    color: #282828;
    font-size: 1.3em;
    padding: 1em 1em 0 0;
    border-top: 1px solid rgba(0,0,0,.15);
    margin: 1em -0.8em 1em -0.5em;
    font-weight: normal;
}

#side-bar .menu-item > a:hover,
#side-bar .collapsible-block-folded .collapsible-block-link:hover {
    background-color: rgba(0,0,0,0.05);
    color: var(--accentColor);
    text-decoration: none;
}

#side-bar .collapsible-block-unfolded-link {
    border-bottom: none;
    padding: 1em 1em 0 0;
    border-top: 1px solid rgba(0,0,0,.15);
    margin: 1em -0.8em 1em -0.5em;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    margin-left: 0;
    font-weight: normal;
    color: #282828;
    font-size: 1.3em;
}

#top-bar div.open-menu a {
    border-radius: 0;
    box-shadow: 0px 1px 4px 0 rgba(0, 0, 0, 0.2), 0px 3px 10px 0 rgba(0, 0, 0, 0.19);
    color: var(--themeColor);
    border: none;
}

#side-bar a:visited,
#top-bar ul li.sfhover a,
#top-bar ul li:hover a,
#login-status ul a,
#login-status ul a:hover {
    color: var(--themeColor);
}

@media (max-width: 767px) {
    #main-content {
        padding: 0;
        margin: 0 5%;
        border-left: none;
    }
    #page-title {
        margin-top: 0.7em;
    }
    #side-bar {
        left: -19em;
    }
    #side-bar:target {
        border: none;
        box-shadow: 1px 0 5px 0 rgba(0,0,0,0.2);
    }
    #side-bar .close-menu {
        transition: width 0.5s ease-in-out 0.1s,
        opacity 1s ease-in-out 0s;
        display: block;
        position: fixed;
        width: 100%;
        height: 100%;
        top: 0;
        right: 0;
        background: rgba(0,0,0,0.3);
        background-position: 19em 50%;
        z-index: -1;
        opacity: 0;
        pointer-events: none;
    }
    #side-bar:target .close-menu {
        width: calc(100% - 19em);
        right: 0;
        left: auto;
        opacity: 1;
        pointer-events: auto;
    }
    #page-content > hr, #page-content > .list-pages-box > .list-pages-item > hr {
        margin: 3em -5.5%;
    }
    #side-bar {
        top: 0;
    }
    #side-bar .heading,
    #side-bar .collapsible-block-folded,
    #side-bar .collapsible-block-unfolded-link {
        padding-left: 1em;
        margin-left: -1em;
    }
    #side-bar .collapsible-block-folded {
        background-position: 1em 2px;
    }
}

/* ---- TABS ---- */

.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: var(--themeColor); /* color between tab list and content */
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background: white; /* tab background */
    border-color: transparent;
    color: black;
    transition: 0.125s;
}

.yui-navset .yui-nav a em {
    background: #f4f4f4;
    box-shadow: 0px 0px 0px rgba(0,0,0,0);
    border-color: white;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
    background: white; /* selected tab background */
    color: var(--themeColor);
}

.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background: white;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a em {
    border-color: transparent;
    box-shadow: 0px 1px 2px 0.5px rgba(0,0,0,0.5);
    background: white;
}

.yui-navset .yui-content {
    background: #f4f4f4; /* content background color */
}

.yui-navset .yui-content,
.yui-navset .yui-navset-top .yui-content {
    border-color: transparent; /* content border */
    border-top-color: transparent; /* different border color */
}

.yui-navset-left .yui-content {
    border-left-color: #b7a9a9; /* different border color */
}

/* ---- INFO BAR ---- */
 body{
     --barColour: var(--themeColor);
}

 .info-container .collapsible-block-content .wiki-content-table{
     width: 100%;
}

/* ---- INFO PANE ---- */

#page-content .creditRate{
    margin: unset;
    margin-bottom: 8px;
}
#page-content .rate-box-with-credit-button {
    background-color: #ffffff;
    border: solid 1px white;
    box-shadow: none;
    border-radius: 0;
}

#page-content .rate-box-with-credit-button .fa-info {
    border: none;
    color: var(--themeColor);
}

#page-content .rate-box-with-credit-button .fa-info:hover {
    background: #ffffff;
    color: var(--accentColor);
}

.rate-box-with-credit-button .cancel {
    border: solid 1px white;
}

#page-content .creditButtonStandalone p a {
  background: var(--themeColor);
}
#page-content .creditButtonStandalone p a:hover {
  background: var(--accentColor);
}

/* ---- PAGE RATING ---- */

.page-rate-widget-box {
    box-shadow: none;
    margin: unset;
    margin-bottom: 8px;
}

.page-rate-widget-box .rate-points {
    background-color: white !important;
    color: var(--themeColor) !important;
    border: solid 1px white;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: white;
    border-top: solid 1px white;
    border-bottom: solid 1px white;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: transparent;
    color: var(--themeColor);
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: white;
    color: var(--accentColor);
}

.page-rate-widget-box .cancel {
    background: transparent;
    background-color: white;
    border: solid 1px white;
}

.page-rate-widget-box .cancel a {
    color: var(--themeColor);
}
.page-rate-widget-box .cancel a:hover {
    background: white;
    color: var(--accentColor);
}

/* ---- PAGE ELEMENTS ---- */

#page-content .wiki-content-table tr th {
    border: solid 1px #bbbbbb;
    background-color: #f4f4f4;
    /* set border for table title */
}

#page-content .wiki-content-table tr td {
    border: solid 1px #bbbbbb;
    /* set border for table content */
}

blockquote,
div.blockquote,
#toc,
.code {
     background-color: #f4f4f4;
     border: solid 1px #bbbbbb;
}

.scp-image-block {
    border: solid 1px #bbbbbb;
    box-shadow: none;
    box-sizing: border-box;
}
.scp-image-block .scp-image-caption {
    background-color: #f4f4f4;
    border-top: solid 1px #bbbbbb;
    color: black;
}

hr {
  background-color: rgba(0, 0, 0, 0.267);
}

/* ---- FOOTER ---- */
#footer { background: transparent; }
#footer a { color: var(--themeColor); }
