}
.ritual hr{ background-color: #ccd2e0; }
.ritual .h3{ color: #ccd2e0; }

.division{
     border: 1px solid rgb(12, 12, 12);
     background: white url(http://scp-wiki.wdfiles.com/local--files/the-great-hippo/scp_trans.png) center no-repeat;
    display: inline-block;
    width: 70%;
}

.ritual .quote-block, .ritualt blockquote{ border: 1px solid rgb(12, 12, 12); }

.log{
  float: center;
  padding: 7px;
  margin: 3px;
  background-color: #fafafa;
  box-shadow: 0 2px 4px rgba(75, 75, 75, .5);
}
.psi{
    border: 1px dashed #444;
    background-color: #f5f5f5;
    padding: 0 0.769em;
    margin: 1em 0;
}

.collapsible-block-link { font-weight: bold; }
.terminal{
    width: @display-width;
    height: @display-height;
    border: 1px solid black;
    border: solid 3px #BBBBBB;
    border-radius: 16px;
    background-color: #20142e;
    margin: 0;
/* black shadow at the top of the terminal */
    background-image:
    radial-gradient(ellipse 1000% 100% at 50% 90%, transparent, #191122);
    background-position: center;
    display: block;
/* shadow around the terminal */
    box-shadow: inset 0 0 10em 1em rgba(0,0,0,0.5);
/* prevents scanline from forming a scrollwheel */
    overflow: hidden;
    padding-bottom: 15px !important;
}
.scanline{
    margin-top: -40%;
    width: @display-width;
    height: 60px;
    position: relative;
    pointer-events: none;
    -webkit-animation: scan 5s linear 0s infinite; /* Safari 4.0 - 8.0 */
    animation: scan 5s linear 0s infinite;
    background: linear-gradient(to bottom, rgba(56, 56, 82,0), rgba(56, 56, 112,0.1)) !important;
}
.text{
    color: rgba(255,255,255,0.8);
    padding-top: 34%;
    padding-left: 3rem;
    padding-right: 3rem;
    font-family: Lucida Console, Monaco, monospace;
}

.terminal2{
    border: 3px solid #bbb;
    border-radius: .976em;
    background-color: #20142e;
    text-align: center;
/* black shadow at the top of the terminal */
    background-image: radial-gradient(ellipse 1000% 100% at 50% 90%, transparent, #191122);
/* shadow around the terminal */
    box-shadow: inset 0 0 10em 1em rgba(0,0,0,0.5);
/* prevents scanline from forming a scrollwheel */
    overflow: hidden;
}
.terminal2 hr{
    background: 1px solid rgba(242, 242, 242, 0.9);
    height: 2px;
}
.scanline2{
    background: linear-gradient(to bottom, rgba(56, 56, 82,0), rgba(56, 56, 112,0.1));
    margin-top: -40%;
    height: 3.659em;
    position: relative;
    pointer-events: none;
    animation: scan 5s linear 0s infinite;
/* Safari 4.0 - 8.0 */
    -moz-animation: scan 5s linear 0s infinite;
    -webkit-animation: scan 5s linear 0s infinite;
}
.terminal-text2{
    padding: 34% 3em 0;
    color: rgba(242, 242, 242, 0.9);
    font-family: Lucida Console, Monaco, monospace;
    font-size: 110%;
}
/* SAFARI 4.0 - 8.0 */
@-webkit-keyframes scan{
    from{ transform: translateY(-10%); }
    to{  transform: translateY(2000%); }
}
.fake-link{
    color: #rgba(242, 242, 242, 0.9)
    cursor: pointer;
}
.fake-link :hover{
    left: 25%;
    color: #e9e7cc;
    width: 50%;
    position: relative;
    background-color: #7f7f7f;
    background: linear-gradient(to right, rgba(0,0,0,0),#7f7f7f,#7f7f7f,rgba(0,0,0,0));
}
.fake-link :hover::before{ content: "▶ "; }

.notice hr{ background: rgb(12, 12, 12); height:  2px; }

