[[module css]]
.bhl-aapa .sentiment-bar {
    border: 1px solid rgb(var(--medium-accent)) !important;
    border-radius:  0px 0px 0px 10px !important;
    box-shadow: 0px 0px 0px 2px #ffb961 !important;
}
.bhl-aapa .neutralbar {
    background-color: rgb(var(--medium-accent)) !important;
    border: 0 !important;
    border-radius:  0px 0px 0px 10px !important;
    color: #ffb961 !important;
}
.bhl-aapa.alldown .downbar-base {
    border-radius:  0px 0px 0px 10px !important;
}
.bhl-aapa .downbar-base {
    background-color: rgba(var(--rating-module-button-negative-color),.8);
}
.bhl-aapa .downbar-text {
    color: #ffb961 !important;
}
.bhl-aapa .upbar {
    background-color: rgba(var(--rating-module-button-plus-color),.8);
    border-radius:  0px 0px 0px 10px !important;
    color: #ffb961 !important;
}
.bhl-aapa .rate-box-with-credit-button {
    background-color: rgb(var(--medium-accent));
    border: 1px solid rgb(var(--medium-accent));
    border-radius: 0px 10px 0px 10px !important;
    box-shadow: 0px 0px 0px 2px #ffb961;
}
.bhl-aapa.credit-rate-with-sentiment-bar:hover .rateBox .rate-box-with-credit-button {
    border-radius: 0px 10px 0px 0px !important;
}
[[/module]]
