[[include :scp-wiki-cn:component:sigma-plus]]
[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+SC');
@import url('https://fonts.googleapis.com/css2?family=Pinyon+Script');

:root {
    --sp_header-logo: url("http://ah-sandbox.wikidot.com/local--files/mcdonalds/SCPI.svg");
    --sp_header-height: 25rem;
    --sp_top-bar-height: 1.6rem;
    --sp_top-bar-gradient-top-color: transparent;
    --sp_top-bar-gradient-bottom-color: transparent;
    --sp_top-bar-link-color: #333;
    --sp_top-bar-link-hover-color: #000;
    --sp_top-bar-link-hover-background: #FAECCC;
    --sp_rate-module-background: #A82;
    --sp_rate-module-sub-color: #FFF;
    --sp_rate-module-button-background: #FAECCC;
    --sp_area-max-width: 45rem;
    --McImageMask: radial-gradient(closest-side, #000 0%, #000 100%, transparent 100%);
}

body {
    font-family: "Noto Sans SC", sans-serif;
    font-size: 0.875rem;
}

@media (min-width: 768px) {
    div.mobile-top-bar {
        display: block;
        height: 0 !important;
    }

    #side-bar {
        display: block;
        position: fixed;
        top: 0;
        left: calc(var(--sp_side-bar-width) * -1 - 3rem);
        width: var(--sp_side-bar-width);
        height: 100%;
        background-color: rgb(184, 134, 134);
        overflow-y: auto;
        z-index: 100;
        padding: 1em 1em 0 1em;
        -webkit-transition: left 0.5s ease-in-out 0.1s;
        -moz-transition: left 0.5s ease-in-out 0.1s;
        -ms-transition: left 0.5s ease-in-out 0.1s;
        -o-transition: left 0.5s ease-in-out 0.1s;
        transition: left 0.5s ease-in-out 0.1s;
    }

    div.mobile-top-bar div.open-menu {
        display: block;
    }

    #top-bar .open-menu a {
        position: fixed;
        top: 0.5em;
        left: 0.5em;
        z-index: 5;
        font-family: 'Nanum Gothic', san-serif;
        font-size: 30px;
        font-weight: 700;
        width: 30px;
        height: 30px;
        line-height: 0.9em;
        text-align: center;
        border: 0.2em solid #888;
        background-color: #fff;
        border-radius: 3em;
        color: #888;
    }

    #top-bar .open-menu a:hover {
        text-decoration: none;
        -webkit-box-shadow: 0 0 20px 3px rgba(153,153,153,1);
        -moz-box-shadow: 0 0 20px 3px rgba(153,153,153,1);
        -ms-box-shadow: 0 0 20px 3px rgba(153,153,153,1);
        -o-box-shadow: 0 0 20px 3px rgba(153,153,153,1);
        box-shadow: 0 0 20px 3px rgba(153,153,153,1);
    }

    #side-bar:target {
        display: block;
        left: 0;
        width: var(--sp_side-bar-width);
        margin: 0;
        z-index: 10;
    }

    #side-bar:target + #main-content {
        left: 0;
    }

    #side-bar:target .close-menu {
        display: block;
        position: fixed;
        width: 100%;
        height: 100%;
        top: 0;
        left: 0;
        background: rgba(0,0,0,0.3) 1px 1px repeat;
        z-index: -1;
    }

    div.mobile-top-bar ul {
        display: none !important;
    }
}

div#content-wrap {
    justify-contents: center;
}

div#header {
    background-image: linear-gradient(to bottom, transparent 0, #FFF 95%);
}

div#header::before {
    grid-row: 1/6;
    grid-column: 1/6;
    width: auto;
}

div#header::after {
    content: "";
    display: block;
    grid-column: 1/6;
    grid-row: 1/6;
    background-image: url("https://ah-sandbox.wikidot.com/local--files/mcdonalds/header.jpg");
    background-size: cover;
    background-position: center top;
    background-repeat: no-repeat;
    z-index: -1;
}

div#header h1 {
    opacity: 0;
    grid-row: 2/6;
    grid-column: 2/4;
    display: block;
    max-width: unset;
    margin: 0;
    padding: 0;
}

div#header h1 a {
    display: block;
    width: 100%;
    height: 100%;
    max-width: unset;
}

div#header h2,
input#search-top-box-input,
div#page-title {
    display: none;
}

div#page-content {
    line-height: 1.75;
    text-align: justify;
    justify-align: both;
}

div#page-content p,
div#page-content li {
    line-height: 1.75;
}

h1, h2, h3, h4, h5, h6 {
    line-height: 1.75;
}

span.ruby {
    height: unset;
    display: inline-flex;
    flex-direction: column-reverse;
    align-items: center;
    vertical-align: top;
}

span.rt {
    display: block;
    margin-top: -0.625em;
    margin-bottom: 0;
    line-height: 1.25;
}

div.hor {
    height: 10rem;
}

div.McWarningPanel {
    width: min(60%, 300px);
    margin: 1rem auto;
    background-color: #FAECCC;
    padding: 0.5rem;
    text-align: center;
    font-size: 0.9rem;
    border: solid 0.2rem #A82;
    border-left: none;
    border-right: none;
}

div.McImage img {
    width: auto !important;
    mask-image: var(--McImageMask);
    mask-size: 100% 100%;
    mask-repeat: no-repeat;
    mask-position: center center;
    -webkit-mask-image: var(--McImageMask);
    -webkit-mask-size: 100% 100%;
    -webkit-mask-repeat: no-repeat;
    -webkit-mask-position: center center;
}

div.McImage div.scp-image-block {
    box-shadow: none;
    border: none;
}

div.McImage div.scp-image-block div.scp-image-caption {
    position: relative;
    border: solid 0.2rem #A82;
    border-left: none;
    border-right: none;
    width: auto !important;
    margin-top: -2rem;
    padding: 0.5rem;
    font-size: 0.925rem;
    font-weight: 700;
    background-color: #FAECCC;
    z-index: 2;
}

div#side-bar,
div.blockquote,
div.scp-image-block div.scp-image-caption,
table.wiki-content-table th {
    background-color: #FAECCC;
}

table.wiki-content-table th {
    text-align: center;
}
[[/module]]

