    background-position: center center;
    background-repeat: no-repeat;
    background-size: auto 100%;
    opacity: 0.25;
}

#side-bar {
    animation: none;
}

#side-bar a, #side-bar a:visited, #side-bar a.newpage {
    color: #371560;
}

#side-bar .side-block {
    background-color: #DECCDF !important;
    box-shadow: 0px 0px 8px #DECCEF;
    border: solid 2px #BF9DE8;
}

#side-bar .side-block .heading {
    margin: 0 auto;
    width: auto;
    color: #593781;
    border-color: #593781;
}

#side-bar .side-block .menu-item > .image{
    filter: hue-rotate(280deg) saturate(90%) brightness(100%);
    -webkit-filter: hue-rotate(280deg) saturate(90%) brightness(100%);
}

#side-bar .collapsible-block-folded {
    background-image: none;
}

#page-title {
    padding-top: 0.5rem;
    display: block;
    color: #593781;
    border-color: #593781;
    font-family: var(--title-font);
    font-weight: bold;
}

a, a:visited, a.newpage {
    color: #593781;
}

a[href="/system:page-tags/tag/meta#pages"] {
    font-weight: bold;
}

#top-bar div {
    width: 100%;
    text-align: center;
}

#top-bar div > ul {
    float: none;
    width: 100%;
    display: flex;
}

#top-bar div > ul > li {
    flex-grow: 1;
}

#top-bar ul li:hover a,
#top-bar ul li.sfhover a {
    color: #593781;
}

#top-bar .open-menu a, #side-bar::before {
    color: #593781;
}

div.no-margin * {
    margin: 0;
}

