color: #FFF;}
.info-container .collapsible-block-unfolded-link a{
color: #FFF;
}
.info-container .collapsible-block-unfolded-link a{
color: #FFF;
}

#page-content > .collapsible-block {
    display: block;
    text-align: center;
    padding: .8em;
    background-color: #e3e3e3;
    border: 0.125rem dashed #b5b5b5;
    border-left: none;
    border-right:none;
}

