}

p,
h1,
h2,
h3,
h4,
h5 {
color:rgb(255,255,255);
}

div#page-content a {
color:rgb(138,43,226);
}

div#page-content span.number,span.lang-cn,div.level {
color:rgb(255,255,255);
}

div#footer,div#license-area,div#options {
display:none;
}

div#footer {
background-color:rgb(0,0,0)
}

div#login-status {
color:red;
}

a#my-account {
display:none;
}

