} */

#account-options {
display:none;
}

#header span {
color:red;
}

.small {
display:none;
}

div#main-content a {animation-fill-mode:forwards;}

div#main-content a:hover {
animation:aahover 0.4s;
color:rgb(0,0,0);
text-decoration:none;
animation-fill-mode:forwards;
}

@keyframes aahover {
from {background:rgba(0,0,0,0)}
to {background-color:rgb(200,180,255);}
}

/*User Not Found*/
.user {
border:solid 1px red;
color:red;
padding:1px;
}

/* content */


div.danger-diamond a {
display:none
}

div.menu-item div.heading p {
color:black;
}

div#side-bar {
background-color:rgba(0,0,0,0.9);
}

#page-content strong,b {
color:#7B68EE
}

#page-content p {
font-size:1.15em;
}

#page-source * {
color:white;
}

/* divs */

.b1 {

background:rgba(0,0,0,0.7);
width:90%;
padding:5px 10px 5px 10px;
border:solid 2px #7B68EE;
box-shadow: 5px 5px 5px rgba(148,0,211,0.2);
height:auto;
margin:0 auto
}

blockquote {
width:90%;
padding:5px 5px 5px 5px;
border-left:solid 2px #7B68EE;
box-shadow: 5px 5px 5px rgba(148,0,211,0.2);
height:auto;
margin:0 auto;
background:#000000;
border-right:none;
border-top:none;
border-bottom:none;
}

.b2 {
background:#000000;
border-right:solid 2px #7B68EE;
border-top:none;
border-bottom:none;
border-left:solid 2px #7B68EE;
width:90%;
padding:5px 10px 5px 10px;
height:auto;
margin:0 auto;
box-shadow: 5px 5px 5px rgba(148,0,211,0.2);
}

.b3 {
background:#000000;
border:solid 1px #7B68EE
width:95%;
padding:0px;
height:auto;
margin:0 auto;
box-shadow: 5px 5px 5px rgba(148,0,211,0.2);
}

.btn1 {
background-color:#8A2BE2;
border-radius:10px;
width:25%;
height:auto;
margin:0 auto;
border:solid 2px #FFFFFF;
color:#FFFFFF;
box-shadow: 0px 0px 10px rgba(148,0,211,0.6);
}

.btn1:hover {
animation:btn1hover 0.4s;
color:rgb(0,0,0);
text-decoration:none;
animation-fill-mode:forwards;
}

@keyframes btn1hover {
from {background:#8A2BE2;box-shadow: 0px 0px 10px rgba(148,0,211,0.6);color:rgb(255,255,255);}
to {background-color:rgb(75,0,130);box-shadow: 0px 0px 15px rgba(148,0,211,1);color:rgb(0,0,0);}
}

