[[module CSS]]
:root:lang(ja) {
    --title: "";
    --subtitle: "";
}

:root {
    --title-size: 1.25rem;
    --subtitle-size: 0rem;
    --user-button-width: 2.25rem;
    --side-bar-button-width: 2.25rem;

    --header-color-one: var(--basalt-secondary-color);
    --header-color-two: var(--basalt-tertiary-color);
    --modal-fader-background-color: 20, 20, 20;
}

div#page-content {
    min-height: unset;
}

div.page-content {
    position: fixed;
    width: 100%;
    height: 100%;
    left: 0;
    top: 0;
    background-color: rgb(var(--basalt-primary-color));
    background-image: url("https://ah-sandbox.wikidot.com/local--files/the-bear-2/logotrans2.svg");
    background-size: auto 80vmin;
    background-position: center center;
    background-repeat: no-repeat;
    z-index: 11;
}

div.page-content p,
div.userName,
div.noUserName {
    display: contents;
}

div.page-content iframe {
    width: 100%;
    height: 100%;
    border: none;
}

div.userName + div.noUserName {
    display: none;
}

div.colmod-content {
    animation: none;
}

li.folded div.colmod-link-top div.foldable-list-container a:nth-child(1) {
    display: block;
    text-align: center;
    font-weight: 900;
    font-size: min(2rem, 6vw);
}

li.unfolded div.colmod-link-top div.foldable-list-container a:nth-child(2) {
    position: fixed;
    display: block;
    font-size: 0;
    color: transparent;
    top: 2rem;
    left: 2rem;
    width: 2rem;
    height: 2rem;
    background-color: rgb(var(--basalt-secondary-color));
    background-image: url("data:image/svg+xml,%3Csvg version='1.1' id='图层_1' xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink' x='0px' y='0px' viewBox='0 0 100 100' style='enable-background:new 0 0 100 100;' xml:space='preserve'%3E%3Cstyle type='text/css'%3E .st0%7Bfill:%23FEFEFE;%7D%0A%3C/style%3E%3Cpath class='st0' d='M79.25,50c0-1.2426758-1.0078125-2.25-2.25-2.25H26.4321289l14.1586914-14.1591797 c0.8789063-0.878418,0.8789063-2.3032227,0-3.1816406c-0.8779297-0.8789063-2.3037109-0.8789063-3.1816406,0l-18,18 c-0.0531006,0.0531006-0.0840454,0.119812-0.1309814,0.177124c-0.0115967,0.013855-0.0206909,0.0291748-0.0320435,0.043335 c-0.1207886,0.1549683-0.2216797,0.3173218-0.2971191,0.4943237c-0.0073853,0.0166626-0.0206299,0.0290527-0.0276489,0.0459595 c-0.0141602,0.0342407-0.013855,0.0708618-0.0262451,0.1054688c-0.0497437,0.1446533-0.0900879,0.2884521-0.1095581,0.4399414 c-0.0143433,0.100708-0.0146484,0.1991577-0.0152588,0.3000488c0.0006104,0.1008911,0.0009155,0.1992798,0.0152588,0.2999878 c0.0194702,0.1516113,0.0598145,0.2956543,0.1096191,0.4404297c0.0123291,0.0344849,0.0120239,0.0709229,0.0261841,0.1050415 c0.006958,0.0168457,0.0202026,0.0291748,0.0275269,0.0457764c0.0755615,0.1774292,0.1766968,0.340332,0.2978516,0.4957275 c0.0109253,0.0136108,0.0197754,0.0285645,0.0310059,0.0419312c0.0470581,0.0575562,0.078125,0.1244507,0.1314087,0.1777954l18,18 C37.8481445,70.0615234,38.4243164,70.28125,39,70.28125s1.1518555-0.2197266,1.5908203-0.6591797 c0.8789063-0.8789063,0.8789063-2.3027344,0-3.1816406L26.4316406,52.28125H77c1.2421875,0,2.25-1.0078125,2.25-2.2504883 c0-0.005249-0.0014648-0.0101318-0.0015259-0.0153809C79.2485352,50.0101318,79.25,50.005249,79.25,50z'/%3E%3C/svg%3E");
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
    transition: ease 0.125s;
    z-index: 12;
}

li.unfolded div.colmod-link-top div.foldable-list-container a:nth-child(2):hover {
    background-color: rgb(var(--basalt-undertone));
}

div.rate-module,
div#page-content div.rate-module div.creditRate,
div#page-content div.rate-module div.rateBox {
    display: contents;
}

div.rate-module div.rate-box-with-credit-button {
    position: fixed;
    top: 2rem;
    right: 2rem;
    z-index: 12;
}

#u-credit-view, #u-credit-otherwise {
    z-index: 13;
}

.fader iframe, .fader a {
    border: none;
}

div#page-options-container {
    max-width: max(25rem, 50%) !important;
    margin-left: auto;
}

div#page-options-bottom a:nth-child(1),
div#page-options-bottom a:nth-child(3),
div#page-options-bottom a:nth-child(5),
div#page-options-bottom a:nth-child(6),
div#page-options-bottom a:nth-child(7),
div#page-options-bottom a:nth-child(8),
div#page-options-bottom-2 a:nth-child(1),
div#page-options-bottom-2 a:nth-child(2),
div#page-options-bottom-2 a:nth-child(3),
div#page-options-bottom-2 a:nth-child(4),
div#page-options-bottom-2 a:nth-child(5),
div#page-options-bottom-2 a:nth-child(7),
div#page-options-bottom-2 a:nth-child(8),
div#page-info,
div.page-watch-options {
    display: none;
}

div.pageComments {
    position: absolute;
    opacity: 0;
    pointer-events: none;
    top: 0;
    left: 0;
    width: 100%;
    height: 100vh;
    background-color: rgba(var(--basalt-primary-color), 0.8);
    backdrop-filter: blur(0.25rem);
    overflow-y: auto;
    transition: ease 0.5s;
    transition-delay: 0.5s;
}

div.comments-box {
    max-width: min(calc(100% - 4rem), var(--main-content-width)) !important;
    margin: 5rem auto 0;
}

div#u-comments:target ~ div.pageComments {
    opacity: 1;
    pointer-events: auto;
}
[[/module]]
