<script type="text/javascript">
    let URL = window.location.href;
    let Name = URL.split('=')[1];
    window.onload = function() {
        var t=0;
        var time=new Date().getHours();
        if (time>=19) {
            t=1;
        }
        else if (time<7) {
            t=1;
        }
        else {
            t=2;
        }
        switch (t) {
            case 1:
                location.href = "//scp-wiki-cn.wdfiles.com/local--code/scp-cn-2801/2?name=" + Name;
                break;
            case 2:
                location.href = "//scp-wiki-cn.wdfiles.com/local--code/scp-cn-2801/3?name=" + Name;
                break;
        }
    }
</script>
