<!DOCTYPE HTML>
<head>
    <meta name="viewport" content="width=device-width, initial-scale=1.0"/>
    <style>
        @import url('//fonts.googleapis.com/css2?family=Noto+Sans+SC:wght@300;400;700;900&display=swap');
        @import url('//fonts.googleapis.com/css2?family=Noto+Serif+SC:wght@300;400;700;900&display=swap');
        @import url('//fonts.googleapis.com/css2?family=Anonymous+Pro:wght@300;400;700;900&display=swap');
        @import url('//fonts.googleapis.com/css2?family=Ma+Shan+Zheng&display=swap');

        :root {
            font-size: 16px;
            line-height: 1.5;

            --title-font: "Noto Serif SC", serif;
            --body-font: "Noto Sans SC", sans-serif;
            --mono-font: "Anonymous Pro", "Noto Serif SC", monospace;

            --base-font-size: 1.1rem;
            --true-font-size: clamp( calc(var(--base-font-size) *.825), calc(var(--base-font-size) *.775 + .195vw), calc(var(--base-font-size) *1.025) );

            --primary-color: 20, 20, 20;
            --text-color: 254, 254, 254;
            --accent-color: 202, 20, 20;
            --secondary-color: 32, 29, 28;
            --tertiary-color: 40, 39, 38;

            --main-content-width: 50rem;
        }

        ::-webkit-scrollbar{
            width: .5rem;
            background: 0 0;
        }

        ::-webkit-scrollbar-track{
            background: rgb(var(--tertiary-color));
        }

        ::-webkit-scrollbar-thumb{
            background-color: rgb(var(--accent-color));
        }

        * {
            max-width: 100%;
            scrollbar-width: thin;
            scrollbar-color: rgb(var(--accent-color)) rgb(var(--tertiary-color));
        }

        body {
            margin: 0;
            padding: 0;
            background-color: rgb(var(--primary-color));
            color: rgb(var(--text-color));
        }

        body.blocked {
            overflow-y: hidden;
            pointer-events: none;
        }

        body.unblocked div#p2-contents {
            display: none;
        }

        div.page-container {
            margin: 0 auto;
            padding: 2rem;
            max-width: var(--main-content-width);
            font-family: var(--body-font);
            font-size: var(--true-font-size);
        }

        div.page-container:not(.page-1) {
            display: grid;
            opacity: 0;
            max-height: 0;
            max-width: unset;
            width: auto;
            overflow: hidden;
            padding: 0;
            grid-template-columns: [full-start] 1fr 2rem [content-start] min(calc(100vw - 4rem), var(--main-content-width)) [content-end] 2rem 1fr [full-end];
        }

        div.page-container:not(.page-1) > * {
            grid-column: content;
        }

        tt {
            font-family: var(--mono-font);
        }

        h1 {
            font-family: var(--body-font);
            font-weight: 700;
            font-size: 2.325em;
            line-height: normal;
            border-bottom: solid 0.2rem rgb(var(--tertiary-color));
            padding: 0.125em 0;
            margin: 0;
            margin-top: 4rem;
            text-align: center;
        }

        h2 {
            font-weight: 900;
            font-family: var(--title-font);
        }

        h4 {
            font-size: 120%;
            font-weight: 900;
        }

        hr {
            height: 0.2rem;
            border: none;
            background-color: rgb(var(--tertiary-color));
        }

        blockquote {
            margin-inline: 0;
            padding: 1rem;
            text-align: left;
            border: solid 0.1rem rgb(var(--tertiary-color));
            background-image: none;
            background-color: transparent;
            box-shadow: -0.2rem 0 0 rgb(var(--accent-color));
        }

        blockquote.warning {
            box-shadow: 0 0.2rem 0 rgb(var(--accent-color));
            text-align: center;
        }

        div.ques blockquote em {
            font-family: 'Ma Shan Zheng', cursive;
            font-style: normal;
        }

        img.image {
            border: solid 0.1rem rgb(var(--accent-color));
            margin-block: 1rem;
            max-width: calc(100% - 0.2rem);
        }

        div.scp-image-block {
            border: solid 0.1rem rgb(var(--tertiary-color));
            box-shadow: 0 0.2rem 0 rgb(var(--accent-color));
            text-align: center;
        }

        div.scp-image-block.block-right {
            margin-left: 1rem;
            margin-bottom: 1rem;
            float: right;
        }

        div.scp-image-block.block-left {
            margin-right: 1rem;
            margin-bottom: 1rem;
            float: left;
        }

        div.scp-image-caption {
            padding: 0.4rem;
            font-size: 80%;
            font-weight: bold;
            line-height: 1.1;
            word-break: break-all;
        }

        div.hor {
            height: 15rem;
        }

        div.hors {
            height: 5rem;
        }

        a.btn {
            position: relative;
            display: inline-block;
            margin: 0.5rem;
            padding: 0.5rem 1rem;
            color: inherit;
            text-decoration: none;
            border: solid 0.1rem rgb(var(--tertiary-color));
            box-shadow: 0 0.2rem 0 rgb(var(--accent-color));
            box-sizing: border-box;
            transition: ease 0.25s;
            pointer-events: auto;
        }

        a.btn.blocked {
            cursor: not-allowed;
        }

        a.btn::after {
            content: "";
            display: block;
            position: absolute;
            width: 100%;
            height: 100%;
            left: 0;
            top: 0;
            pointer-events: none;
            transition: ease 0.25s;
        }

        a.btn:hover {
            background-color: rgb(var(--tertiary-color));
        }

        a.btn.zero:hover {
            color: rgb(var(--primary-color));
            background-color: rgba(var(--accent-color), 0.8);
            border-color: rgb(var(--accent-color));
        }

        a.secure.btn::after {
            animation: btn 1s infinite;
        }

        a.secure.btn:hover::after {
            opacity: 0;
        }

        a.secure.btn.hovered::after {
            opacity: 0;
        }

        a.btn.clicked::after,
        a.secure.btn.clicked::after {
            opacity: 1;
            animation: btn1 1s forwards;
        }

        a.btn.clicked2::after {
            opacity: 1;
            animation: btn 1s forwards;
        }

        div.page-container.disappear {
            animation: disappear 2s forwards;
            pointer-events: none;
        }

        div.page-container.appear {
            animation: appear 2s forwards;
            animation-delay: 2.5s;
        }

        div#questionnaire {
            height: 100vh;
            overflow-y: auto;
            text-align: center;
            grid-column: full;
        }

        div#questionnaire div.tips {
            color: rgba(var(--text-color), 0.6);
            margin: calc(5vh + 5rem) auto 0 auto;
            max-width: min(calc(100% - 4rem), var(--main-content-width));
        }

        div.ques {
            display: none;
            margin: 0 auto;
            max-width: min(calc(100% - 4rem), var(--main-content-width));
            pointer-events: auto;
        }

        div.quescon {
            padding: 5vh 0;
        }

        div.ques div.choices {
            margin-top: 5vh;
            margin-inline: -1rem;
            max-width: calc(100% + 2rem);
        }

        div.ques.displayed {
            display: block;
            opacity: 0;
            max-height: 0;
            overflow: hidden;
            animation: appear 0.5s forwards;
            animation-delay: 0.5s;
        }

        div.ques.undisplayed {
            display: block;
            animation: disappear2 0.5s forwards;
        }

        div#p2-contents.undisplayed {
            display: none;
        }

        table.wiki-content-table {
            border-collapse: collapse;
        }

        table,
        table.wiki-content-table th,
        table.wiki-content-table td {
            border: solid 1px rgb(var(--tertiary-color));
            background: transparent;
        }

        table.wiki-content-table th,
        table.wiki-content-table td {
            padding: 0.75rem;
        }

        table.wiki-content-table th {
            color: rgb(var(--primary-color));
            text-align: center;
            border: solid 1px rgb(var(--accent-color));
            background-color: rgba(var(--accent-color), 0.75);
        }

        @keyframes btn {
            0% {
                background-color: rgba(254, 254, 254, 0.6);
                left: 0rem;
                top: 0rem;
                width: 100%;
                height: 100%;
            }

            100% {
                background-color: rgba(254, 254, 254, 0);
                left: -1rem;
                top: -1rem;
                width: calc(100% + 2rem);
                height: calc(100% + 2rem);
            }
        }

        @keyframes btn1 {
            0% {
                background-color: rgba(254, 254, 254, 0.6);
                left: 0rem;
                top: 0rem;
                width: 100%;
                height: 100%;
            }

            100% {
                background-color: rgba(254, 254, 254, 0);
                left: -1rem;
                top: -1rem;
                width: calc(100% + 2rem);
                height: calc(100% + 2rem);
            }
        }

        @keyframes disappear {
            0% {
                opacity: 1;
                max-height: unset;
                overflow: visible;
                padding: 2rem;
            }

            99% {
                opacity: 0;
                max-height: unset;
                overflow: visible;
                padding: 2rem;
            }

            100% {
                opacity: 0;
                max-height: 0;
                overflow: hidden;
                padding: 0;
            }
        }

        @keyframes disappear2 {
            0% {
                opacity: 1;
                max-height: unset;
                overflow: visible;
            }

            99% {
                opacity: 0;
                max-height: unset;
                overflow: visible;
            }

            100% {
                opacity: 0;
                max-height: 0;
                overflow: hidden;
            }
        }

        @keyframes appear {
            0% {
                opacity: 0;
                max-height: 0;
                overflow: hidden;
            }

            1% {
                opacity: 0;
                max-height: unset;
                overflow: visible;
            }

            100% {
                opacity: 1;
                max-height: unset;
                overflow: visible;
            }
        }
    </style>

    <script>
        let URL = window.location.href;
        let Name = URL.split('=')[1];

        window.onload = function() {
            if (Name == 'undefined') {
                document.getElementById('userName').innerHTML = 'TGl0dGxlYmVhcg==';
            }
            else {
                document.getElementById('userName').innerHTML = decodeURI(Name);
            }
        }

        function btn(x) {
            document.getElementById(x).className = x + ' btn clicked';
            void document.getElementById(x).offsetWidth;
        }

        function btn2(x) {
            if (document.getElementById(x).className != 'btn clicked') {
                document.getElementById(x).className = 'btn clicked';
                void document.getElementById(x).offsetWidth;
            }
            else {
                document.getElementById(x).className = 'btn clicked2';
                void document.getElementById(x).offsetWidth;
            }
        }

        function overlay() {
            document.getElementById("p1").className += ' disappear';
            document.getElementById("p2").className += ' appear';
        }

        //交叉观察器，参考自MDN

        function sleep(time) {
            return new Promise((resolve) => setTimeout(resolve, time));
        }

        async function blockWindow() {
            document.getElementById("viewBody").className += 'blocked';
            await sleep(100);
            document.getElementById("questionnaire").scrollIntoView({block: "end", behavior: "smooth"});
            //document.getElementById('p2-contents').className += ' undisplayed';
            document.getElementById("q0").className += ' displayed';
        }

        window.addEventListener (
            "load",
            (event) => {
                let target = document.querySelector("#questionnaire");
                let callback = (entries, observer) => {
                    entries.forEach((entry) => {
                        if (entry.isIntersecting) {
                            blockWindow();
                        }
                    })
                }
                let observer = new IntersectionObserver(callback);
                observer.observe(target);
            },
            false,
        )

        //问卷相关

        function play(x) {
            document.getElementById(x).play();
        }

        function pChange(x, y) {
            document.getElementById(x).className = 'ques displayed';
            document.getElementById(y).className = 'ques undisplayed';
        }

        function choice(x, y, z, a) {
            play(y);
            btn2(a);
            pChange(x, z);
        }

        function block(x,y) {
            play(x);
            document.getElementById(y).className += ' blocked';
        }

        function theend(x, y, z) {
            play(x);
            btn2(z);
            document.getElementById(y).className = 'ques undisplayed';
        }
    </script>
</head>

