div.rate-module,
div#page-content div.rate-module div.creditRate,
div#page-content div.rate-module div.rateBox {
    display: contents;
}

div.rate-module div.rate-box-with-credit-button {
    position: fixed;
    top: 2rem;
    right: 2rem;
    z-index: 12;
}

