#side-bar a:visited{
    color: #B0C4DE !important;}
a:visited {
    color: #B0C4DE !important;}*/

.vpoem{
    text-indent: 0;
    -webkit-writing-mode: vertical-rl;
    writing-mode: vertical-rl;
    margin: auto;
    margin-bottom:0.5em;
    font-size:2.5em;
    font-family: 'KaiTi', 'MS PMincho', 'STKaiti', 'HiraMinProN-W6', 'Hiragino Kaku Gothic Pro', 'MS PGothic', serif;
}

.vpoem p{
    line-height: 1.4;
    margin:0 auto;
}

.vpoem tt{
    vertical-align: middle;
font-family: 'KaiTi', 'MS PMincho', 'STKaiti', 'HiraMinProN-W6', 'Hiragino Kaku Gothic Pro', 'MS PGothic', serif;
}

@media screen and (max-width: 767px) {
.vpoem{
    font-size:1.5em;
}
}

.vpoem{
    margin-top: 0.5em!important;
}

.scp-image-block {
    border-color: #E1FFFF; /*插图边框颜色*/
}

#main-content .page-tags {
    display: none;
}

div.pseudo-tags ul {
    font-size: 85%;
    border-top: 1px solid #BBB;
    display: inline-block;
    margin: 10px 0 0;
    padding: 0 2px;
    list-style: none;
}

div.pseudo-tags li {
    color: #b01;
    text-decoration: none;
    display: inline-block;
    margin: 0 1px;
    cursor: pointer;
}

div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    background-color: rgba(36, 73, 130, 0.83);
}

div#header {
    background: url(https://i.loli.net/2020/06/02/KlvCsbJ6Beuiag3.png) -10px 0px no-repeat;
    background-size: 120px,120px;
}



