<script type="text/javascript" language="javascript">
var timer=null;
var i = 1;
function displayClock(num){
    if(num<10){
        return "0"+num;
    } else {
        return num;
    }
}
function startAnim(){
    var time = new Date();
    var year = displayClock(time.getFullYear());
    var month = displayClock(time.getMonth()+1);
    var day = displayClock(time.getDate());
    var hours = displayClock(time.getHours());
    var minutes = displayClock(time.getMinutes());
    var seconds = displayClock(time.getSeconds());
    var apm="AM";
    if (hours>12) {
        hours -= 12;
        apm="PM";
        if (hours<10) {
            hours="0"+hours;
        }
    }
    sig_2.innerHTML=day+"/"+month+"/"+year+"<br>"+hours+":"+minutes+":"+seconds+"&nbsp;"+apm;
    timer = setTimeout("startAnim()", 1000);
    blinkAnim();
}
function blinkAnim(){
    $("#sig_1 span").css("visibility", "hidden");
    $("#sig_3").animate({opacity: "hide"}, 500, function() {$("#sig_3").css("display", "")});
    setTimeout(function() {
        $("#sig_1 span").css("visibility", "");
    }, 500);
}
timerToggle = setInterval(function() {
    $("#img_"+i).css("display", "none");
    if (i==3) i = 1;
    else i += 1;
    $("#img_"+i).css("display", "");
    if (i==1) $("#sig_3").css({"top": "36.4vw", "left": "5.9vw"});
    if (i==2) $("#sig_3").css({"top": "50vw", "left": "8.3vw"});
    if (i==3) $("#sig_3").css({"top": "45.7vw", "left": "20vw"});
}, 5000);
</script>
