[[module css]]
/*版头*/
#header {
    background-image:url(https://scpsandboxcn.wikidot.com/local--files/acdbanshi/%E5%BE%BD%E6%A0%87.png);
    background-size:105px;
}

div#container-wrap {
    background-image: url(https://scpsandboxcn.wikidot.com/local--files/acdbanshi/background.jpg);
    background-repeat: repeat-x;
}

#header h1 a {
    display: block;
    margin: 0;
    padding: 80px 0 25px;
    line-height: 10px;
    max-height: 30px;
    color: #eee;
    background: transparent;
    font-family: BauhausLTDemi, Simhei, Arial, sans-serif;
    font-size: 180%;
    text-decoration: none;
    letter-spacing: 0.9px;
    text-shadow: none;
}
#header h1 a::before {
    content: "异常管理署";
    margin: 0 10px;
    font-size: 115%;
    font-family: 华文行楷,BauhausLTDemi, Simhei, Arial, sans-serif;
    color: #000000;
    text-shadow: none;
}
#header h1 a span{
    display: block;
    content: "";
    visibility: hidden;
    color:#fff;
}
#header h2 {
    margin-left: 120px;
    padding: 0;
    clear: left;
    float: left;
    font-size: 105%;
    max-height: 38px;
    color:trtransparent;
}
#header h2 span {
    display:block;
    margin:0 15px;
    padding: 15px 0;
    line-height: 15px;
    max-height: 30px;
    font-weight: bold;
    color:transparent;
    text-shadow: none;
}
#header h2 span::before {
    content: "探明异常，控制威胁";
    display: block;
    padding: 5px 0;
    line-height: 15px;
    max-height: 30px;
    font-weight: bold;
    color: #000000;
    font-size: 135%;
    font-family: 华文楷体,BauhausLTDemi, Simhei, Arial, sans-serif;
}
/*个人简介*/
.owindow {
    background-color: white;
    border: 2px solid #483d8b;
    text-align: left;
    position: absolute;
    color: #696969;
}
a.btn.btn-primary {
    color: #696969;
}
a.btn.btn-danger.btn-small.btn-sm {
    color: #696969;
}
.owindow .button-bar a {
    margin: 0.2em 0.2em;
    padding: 0.1em 1em;
    text-decoration: none;
    background-color: #696969;
    border: 1px solid #000000;
    color: #000000;
}
/*个人用户*/
#login-status a {
    background: transparent;
    color: black;
    text-decoration: none;
}
#login-status ul a {
    color: black;
    background: transparent;
    transition: 1s all;
}
#account-options li {
    display: block;
    margin: 0;
    padding: 0;
    border: solid 1px #000000;
    transition: 0.5s all;
}
#account-options {
    position: absolute;
    -moz-box-sizing: border-box;
    width: 15em;
    right: 0;
    padding: 0;
    z-index: 40;
    background-color: white;
    border: 1px solid #483d8b;
}
#login-status ul a:hover {
    color: #000000;
    background: #696969;
}
/*顶栏*/
#top-bar ul li.sfhover a, #top-bar ul li:hover a {
    color: #ac0011;
    border-left: solid 1px rgba(64,64,64,1);
    border-right: solid 1px rgba(64,64,64,1);
}
#top-bar ul li ul a, #top-bar a:hover {
    color: rgba(0,0,0,0);
    background: rgba(0,0,0,0);
}
#top-bar ul li a {
    border-left: solid 1px rgba(64,64,64,.1);
    border-right: solid 1px rgba(64,64,64,.1);
    text-decoration: none;
    padding-top: 10px;
    padding-bottom: 10px;
    line-height: 1px;
    max-height: 1px;
    overflow: hidden;
    transition: weight 2s,height 2s,background 1s,color 1s;
}
#top-bar ul li.sfhover a:hover, #top-bar ul li:hover a:hover {
    background: #C0C0C0;
    text-decoration: none;
    color: #f3f3f3;
}
#top-bar ul li ul {
    border: solid 1px #C0C0C0;
    box-shadow: 0 2px 6px #d9ecf9;
    border-top: 0;
}
#top-bar ul li.sfhover ul li a, #top-bar ul li:hover ul li a {
    border-width: 0;
    width: 150px;
    border-top: 1px solid #C0C0C0;
    line-height: 160%;
    height: auto;
    max-height: none;
    padding-top: 0;
    padding-bottom: 0;
}
/* 评分栏 */
.page-rate-widget-box .rate-points,
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown,
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a,
.page-rate-widget-box .cancel a {
  background-color: #FAFAFA!important;
  border: 0;
  color: #000000!important;
  text-transform: capitalize;
}
div.page-rate-widget-box {
    background-color: #fbfbfb!important;
    border: solid #000000 1px!important;
    border-radius: 0px;
    box-shadow: none;
}
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel {
    background-color: #fbfbfb!important;
    border: 1px #fbfbfb;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    background-color: #fbfbfb!important;
}
div.page-rate-widget-box span.cancel a,
div.page-rate-widget-box span.rateup a,
div.page-rate-widget-box span.ratedown a {
    color: #000000!important;
}
div.page-rate-widget-box span.rateup a:hover {
    background: #C0C0C0!important;
    color: #fbfbfb!important;
}
div.page-rate-widget-box span.ratedown a:hover {
    background: #ff0000!important;
    color: #fbfbfb!important;
}
div.page-rate-widget-box span.cancel a:hover {
    background: #888888!important;
    color: #fbfbfb!important;
    border-radius: 0px;
}
/*搜索栏*/
#search-top-box-form input[type="submit"] {
    border: solid 1px #000000;
    border-radius: 5px;
    padding: 2px 5px;
    font-size: 90%;
    font-weight: bold;
    color: #000000;
    background: transparent;
    box-shadow: none;
    cursor: pointer;
}
#search-top-box-input {
    border: solid 1px #000000;
    border-radius: 5px;
    color: #000000;
    background-color: transparent;
    box-shadow: none;
}
#search-top-box-input:hover, #search-top-box-input:focus {
    border: solid 1px #000000;
    color: #000000;
    background-color: transparent;
    box-shadow: none;
}
#search-top-box input.empty {
    color: #000000;
}
#search-top-box-form input[type="submit"]:hover, #search-top-box-form input[type="submit"]:focus {
    border: solid 1px #000000;
    color: #000000;
    text-shadow: 0 0 1px rgba(255,255,255,.25);
    background: transparent;
    box-shadow: none;
}
/*文章部分*/
h1, #page-title {
    color: #000000;
    padding: 0 0 0.25em;
    margin: 0 0 0.6em;
    font-weight: normal;
}
#page-content {
    color: #000000;
}
#page-title {
    border-color: #000000;
}
/*侧栏*/
#side-bar .side-block {
    padding: 10px;
    border: 1px solid #000000;
    border-radius: 0px;
    box-shadow: none;
    background-image: linear-gradient(0deg,#FAFAFA,#FAFAFA);
    margin-bottom: 15px;
    transition: 1s all;
}
#side-bar div.menu-item a {
    font-weight: bold;
    color: #000000;
    transition: 1s all;
    text-decoration: none;
}
#side-bar div.menu-item a:hover {
    color: #b01;
}
#side-bar .collapsible-block-link {
    margin-left: 15px;
    font-weight: bold;
    color: #000000;
    transition: 1s all;
    border: none;
    background: none;
}
#side-bar .collapsible-block-link:hover {
    color: #b01;
    text-decoration: none;
}
#side-bar .heading {
    color: #000000;
    font-size: 8pt;
    font-weight: bold;
    border-bottom: solid 1px #000000;
}
hr {
    margin: 1em 2em;
    padding: 0;
    height: 1px;
    border: none;
    color: #DDD;
    background-color: #000000;
}
/* ---- RATE MODULE ---- */

.page-rate-widget-box .rate-points,
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown,
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a,
.page-rate-widget-box .cancel a {
  background-color: transparent !important;
  border: 0;
  color: #000000!important;
  text-transform: capitalize;
}
div.page-rate-widget-box {
    background-color: #fbfbfb!important;
    border: solid #000000 1px!important;
    border-radius: 0px;
    box-shadow: none;
}
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel {
    background-color: #fbfbfb!important;
    border: 1px #fbfbfb;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    background-color: #fbfbfb!important;
}
div.page-rate-widget-box span.cancel a,
div.page-rate-widget-box span.rateup a,
div.page-rate-widget-box span.ratedown a {
    color: #000000!important;
}
div.page-rate-widget-box span.rateup a:hover {
    background: #C0C0C0!important;
    color: #fbfbfb!important;
}
div.page-rate-widget-box span.ratedown a:hover {
    background: #ff0000!important;
    color: #fbfbfb!important;
}
div.page-rate-widget-box span.cancel a:hover {
    background: #888888!important;
    color: #fbfbfb!important;
    border-radius: 0px;
}

/* ---- INFO RATE MODULE ---- */

.rate-box-with-credit-button > div.page-rate-widget-box {
     border-width: 0px!important;
}
#page-content .rate-box-with-credit-button {
    background-color: #f4f4f4;
    border: solid 1px #000000;
    border-radius: 0;
    box-shadow: none;
}
#page-content .rate-box-with-credit-button .creditButton p a {
    border-left-color: transparent;
}
 #page-content .rate-box-with-credit-button .page-rate-widget-box .cancel {
    border-radius: 0;
}
#page-content .rate-box-with-credit-button .page-rate-widget-box .rate-points {
    border-left: 0;
}
 .rate-box-with-credit-button .page-rate-widget-box .cancel a:hover {
    border-radius: 0;
}
 #page-content .rate-box-with-credit-button .fa-info {
     color: #000000;
}
#page-content .rate-box-with-credit-button .fa-info:hover {
     color: #ac0011;
}
#page-content .creditButtonStandalone p a {
    background-color: #f4f4f4;
    border: solid 1px #000000;
    border-radius: 0;
    box-shadow: none;
    color: #000000;
}
#page-content .creditButtonStandalone p a:hover {
    color: #ff0000;
}

#page-content .modalbox {
     background: #f4f4f4 !important;
     color: #000000;
     box-shadow: none;
}
.close-credits,
.credit-back {
     filter: hue-rotate(240deg);
}
/*超链接*/
a {
    color: #ac0011;
}
a:visited {
    color: #ac0011;
}
/*网页适应性*/
@media (max-width: 497px) {
.open-menu a {
    position: fixed;
    top: 0.5em;
    left: 0.5em;
    z-index: 15;
    font-family: 'Nanum Gothic', san-serif;
    font-size: 30px;
    font-weight: 700;
    width: 30px;
    height: 30px;
    line-height: 0.9em;
    text-align: center;
    border: 3px solid #000000 !important;
    background-color: transparent !important;
    border-radius: 5px;
    color: #000000 !important;
    box-shadow: 0px 0px 20px 3px #C0C0C0;
    transition: 0.5s all;
}
.open-menu a:hover {
    text-decoration: none !important;
    box-shadow: 0px 0px 20px 3px #C0C0C0;
}
#header {
    background-position: 0 3em;
}
#header h1 a::before {
    content: "异常管理署";
    margin: 0 30px;
    font-size: 48%;
    font-family: 华文行楷,BauhausLTDemi, Simhei, Arial, sans-serif;
    color: #000000;
    text-shadow: none;
}
#header h2 span {
    display: block;
    margin: 0 0px;
    padding: 15px 0px;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}
#header h2 span::before {
    content: "探明异常，控制威胁";
    display: block;
    padding: 0px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: #000000;
    font-size: 90%;
    font-family: 华文楷体,BauhausLTDemi, Simhei, Arial, sans-serif;
    position: absolute;
    bottom: 60px;
    left: 100px;
}
}
[[/module]]


