    /* CSS duplicated from Wikidot proper -_- */
    body {
     font-family: verdana, arial, helvetica, sans-serif;
     font-size: 12.8px; /* 16px * 0.8 */
     color: #333;
     line-height: 141%;
    }

    .classified-info {
     display: none;
    }

    blockquote {
     border: 1px dashed #999;
     padding: 0 12.8px;
