function containment()
{
if(cnum2<cstring.length){
cnum2++;

}
window.setTimeout(cwait,1);
}

function cwait(){
document.getElementById("cbox").value= cstring.substring(cnum1, cnum2);
}


function cdel(){
cnum1++;
if(cnum1==cnum2){
cnum1=0;
cnum2=0;
}
document.getElementById("cbox").value= cstring.substring(cnum1, cnum2);
}

function myFunction() {
  var x = document.getElementById("cbox").value;
if(cstring==x){
if (confirm("是否提交？")) {
window.alert("您有一封新邮件！") ;
document.getElementById("rep").style.display = "none";
