/*
    Y2K Theme
    [2020 Wikidot Theme]
    By etoisle
    Based on SCP Sigma 9 Theme created by Aelanna and Dr Devan
*/

.scp-image-block.block-right {
	margin: 0 2em 1em;
}

body {
	background-attachment: fixed;
	color: #000000;
	font-family: "Microsoft Sans Serif", "宋体";
	font-size: 14px;
	image-rendering: pixelated;
	word-break: unset;
	--themeColor: #008484;
	--accentColor: #008484;
}

a,
#interwiki .side-block div.menu-item a {
	color: rgb(0, 0, 255);
}

a:visited {
	color: #800080;
}

h1 {
	color: #000084;
	font-family: "Microsoft Sans Serif", "宋体";
}

h2,
h3,
h4,
h5,
h6 {
	font-family: "Microsoft Sans Serif", "宋体";
}

blockquote, 
div.blockquote {
	border: #DFDFDF outset 2px;
	background-color: #C0C0C0;
	color: black;
	color: #000;
}

#account-topbutton{
	border: 1px solid #000 !important;
}

div#container-wrap {
	background-image: url('https://scp-wiki.wdfiles.com/local--files/theme%3Ay2k/bg.png');
}

div#header {
	background-image: url('https://scp-wiki.wdfiles.com/local--files/theme%3Ay2k/logo.png');
	image-rendering: pixelated;
}

.scp-image-block {
	box-shadow: 4px 4px 0 0 rgba(0, 0, 0, 0.2);
	border-radius: 0;
	margin: 0 2em 1em;
}

div#side-bar div.side-block,
#interwiki div.side-block {
	background: #C0C0C0;
	border: #DFDFDF outset 2px;
	box-shadow: 4px 4px 0 0 rgba(0, 0, 0, 0.2);
	border-radius: 0;
}

div#side-bar div.side-block div.heading,
#interwiki div.side-block div.heading,
div#side-bar div.collapsible-block-unfolded-link a.collapsible-block-link,
div#side-bar div.collapsible-block-unfolded-link a.collapsible-block-link:hover {
	color: black;
	border-color: black;
}

div#side-bar div.collapsible-block-folded {
        background-image: none;
}

div.yui-navset div.yui-content {
	color: #000000;
	background: #C0C0C0;
	border-left: #DFDFDF outset 2px;
	border-right: #DFDFDF outset 2px;
	border-bottom: #DFDFDF outset 2px;
	border-top: transparent 0;
}

.yui-navset .yui-nav a,
div.yui-navset div.yui-navset-top .yui-nav a {
	/* protect nested tabviews from other orientations */
	border: #DFDFDF outset 2px;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a em,
.yui-navset .yui-nav a em,
.yui-navset .yui-navset-top .yui-nav a em {
	border-color: transparent;
}

.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
	border-color: #008484;
}

div.yui-navset ul.yui-nav a,
div.yui-navset div.yui-navset-top ul.yui-nav a {
	background: #C0C0C0;
	border-left: #DFDFDF outset 2px;
	border-right: #DFDFDF outset 2px;
	border-top: #DFDFDF outset 2px;
	border-bottom: transparent 0;
}

div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav a:focus {
	background: #C0C0C0;
	border-left: #DFDFDF inset 2px;
	border-right: #DFDFDF inset 2px;
	border-top: #DFDFDF inset 2px;
	border-bottom: transparent 0;
}

div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:focus,
div.yui-navset ul.yui-nav .selected a:hover {
	background: #008484;
	border-left: #DFDFDF outset 2px;
	border-right: #DFDFDF outset 2px;
	border-top: #DFDFDF outset 2px;
	border-bottom: transparent 0;
}

div.open-menu a {
	color: black !important;
	border-color: black !important;
}

div.page-rate-widget-box {
	box-shadow: 4px 5px 0 0 rgba(0, 0, 0, 0.2);
	border-radius: 0 !important;
}

div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel {
	background-color: #C0C0C0 !important;
	border: #DFDFDF outset 2px !important;
	border-radius: 0 !important;
}

div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
	border: #DFDFDF outset 2px !important;
}

div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel a,
div.page-rate-widget-box span.cancel a:hover {
	color: #000000;
}

div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
	background-color: #C0C0C0 !important;
}

div.page-rate-widget-box span.rateup a,
div.page-rate-widget-box span.ratedown a {
	color: #000000!important;
}

div.page-rate-widget-box span.cancel a:hover,
div.page-rate-widget-box span.rateup a:hover,
div.page-rate-widget-box span.ratedown a:hover {
	background: transparent !important;
	color: #008484 !important;
}


/* Info Pane */

.rate-box-with-credit-button {
	background-color: #C0C0C0 !important;
	border: #DFDFDF outset 2px !important;
	box-shadow: 4px 4px 0 0 rgba(0, 0, 0, 0.2) !important;
	border-radius: 0 !important;
}

.rate-box-with-credit-button .fa-info {
	border: transparent 0px !important;
	border-radius: 0 !important;
	color: #000000 !important;
}

.rate-box-with-credit-button .fa-info:hover {
	background: transparent!important;
	color: #008484 !important;
}

.rate-box-with-credit-button .cancel {
	border: #DFDFDF outset 2px !important;
}

.close-credits,
.credit-back {
	filter: hue-rotate(288deg);
}

.modalbox{
    background: #fff !important;
    border: #000 solid 1px !important;
    border-radius: 0px !important;
    box-shadow: 4px 4px 0 0 rgba(0, 0, 0, 0.2) !important;
}

#side-bar a:visited,
#interwiki a:visited,
#top-bar a,
#top-bar ul li.sfhover a,
#top-bar ul li:hover a,
#login-status ul a,
#login-status ul a:hover {
	color: #800080;
}

#page-title {
	color: #000084;
}

#login-status,
#login-status a {
	color: white;
}

table.wiki-content-table td {
	border: 1px solid #000;
	background-color: #FFFFFF;
}

table.wiki-content-table th {
	border: 1px solid #000;
	background-color: #DFDFDF;
}

@media (max-width: 767px) {
	#side-bar {
		background-color: #C0C0C0;
	}
}

.side-block .menu-item > .image,
#interwiki .side-block .menu-item img  {
	filter: hue-rotate(250deg);
}

#footer {
	background: transparent;
}

#search-top-box-input,
#search-top-box-input:hover,
#search-top-box-input:focus,
#search-top-box-form input[type=submit],
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
	border: #DFDFDF outset 2px;
	background: #C0C0C0;
	box-shadow: none;
	border-radius: 0;
	color: #000000;
}

#search-top-box input.empty {
	color: #8b8b8b;
}

hr {
	margin: 1em 2em;
	padding: 0;
	height: 0;
	border-top: #F0F0F0 groove 2px;
	/*color: #555545;*/
	/*background-color: #555545;*/
}

#header h1 span {
	font-family: "Microsoft Sans Serif", "宋体";
	color: #ffffff;
	text-shadow: 3px 3px 0 rgba(0, 0, 0, 0.6);
}

#header h2 span::before {
	font-family: "Microsoft Sans Serif", "宋体";
	content: '';
	color: #ffffff;
	text-shadow: none;
}

#header h1 a,
#header h2 span {
	color: transparent;
	text-shadow: none;
}

.scp-image-block.block-right {
	margin: 0 2em 1em;
}

.code {
	border: 1px solid #000000;
	background-color: #ffffff;
	font-family: 'Andale Mono', 'Courier New', Courier, monospace;
	padding: 3px;
	margin: 0.2em 0;
	overflow: auto;
}

.interwiki {
	font-family: "Microsoft Sans Serif", "宋体";
	background-color: #C0C0C0;
	border: #DFDFDF outset 2px;
	box-shadow: 4px 4px 0 0 rgba(0, 0, 0, 0.2);
}

#top-bar ul li ul {
    border: solid 1px #000;
    box-shadow: 4px 4px 0 0 rgba(0, 0, 0, 0.2);
}

