[[module CSS]]

@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+SC:wght@300;400;700;900&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans:wght@300;400;700;900&display=swap');

div#page-content {
    color: #000;
    font-size: 0.9375rem;
    font-family: "Noto Sans SC", "Noto Sans", sans-serif;
}

span.black {
    background-color: #000;
    user-select: none;
}

div.scp-image-block div.scp-image-caption,
div.scp-image-block img {
    width: auto !important;
}

/* anchor */

a:link, a:visited {
    color: inherit;
    text-decoration: currentcolor solid underline;
}
a:hover, a:active {
    background-color: #FFF;
    color: #000;
    text-decoration: #111 solid underline;
}

/* other items */

h1, h2, h3, h4, h5, h6 { text-align: center; }

li:not(:first-child) {margin-top: 0.75em;}

ul, ol {padding-left: 10px;}

/* specified for page */

a#more-options-button, a#view-source-button {
  color: red;
  font-weight: bold;
}

ol li:nth-child(-3n+4) {
  font-size: 0;
}

ol li:last-child {
  font-size: 0;
  margin-top: 1.41rem;
}

ol li:last-child::marker {
  font-size: 0.9375rem;
}

ol li:last-child::after {
  content: "2007年3月4日，徐繁缕女士于其在月港市的住所中失踪。这起案件的调查团队并没有在住所中发现来自外部的侵入痕迹，也没有在探访过程中得知其近期内有长时间离开住所的计划或意愿。值得注意的是，调查团队在阳台上发现了一件损坏的家居饰品：阳台顶部通过铁环悬挂着一副勾牌，与其相连接的八条提线全部从底部断裂；阳台地面上落有一件鸟笼，笼顶残留有小段捆绑在铁架上的提线；鸟笼的出入口因落地时的剧烈碰撞打开，笼中存放着一个已经倾倒的沙盘。对这起案件的进一步调查正在进行中。";
  font-size: 0.9375rem;
  display: inline-block;
  vertical-align: top;
  margin-top: -0.66rem;
  line-height: 141%;
  width: 100%;  
}

[[/module]]
