[[module css]]
div.creditButtonStandalone>p>a::after, div.rate-box-with-credit-button .creditButton>p>a::after {
    filter: invert(1);
}
.nullify {
    display: flex;
    justify-content: center;
    outline: 2px solid #333;
    margin: 10px;
    padding: 0;
    font-size: 1.5em;
    box-shadow: 0 0 5px 1px black;
    -webkit-backdrop-filter: sepia(1) brightness(.2);
    backdrop-filter: sepia(1) brightness(.2);
    border: 1px solid #3a3a3a;
    transition: all .2s linear;
}
.nullify:hover {
    -webkit-backdrop-filter: sepia(1) brightness(.8);
    backdrop-filter: sepia(1) brightness(.3);
}
#top-bar ul li ul {
    top: inherit !important;
}
@media (min-width: 767px) {
div.mobile-top-bar {
    display: none !important;
}
}
:root:lang(cn) {
  --header-title: "SCP 基金会";  --header-subtitle: "控制，收容，保护";
}
[[/module]]
