/* ---- INFO PANE ---- */
 #page-content .creditRate{
    margin: unset;
    margin-bottom: 4px;}#page-content .rate-box-with-credit-button {
    background-color: #FBFBFB;
    border: solid 1px #FBFBFB;
    box-shadow: none;
    border-radius: 0;
    padding: 2px;
    border: solid 1px #A2A9B1;}
 #page-content .rate-box-with-credit-button .fa-info {
    border: none;
    color: #202122;}
 #page-content .rate-box-with-credit-button .fa-info:hover {
    background: #DDDDFF;
    color: #FBFBFB;}
 .rate-box-with-credit-button .cancel {
    border: solid 1px #FBFBFB;}
 .rate-box-with-credit-button .page-rate-widget-box {
    border: none;}
 #page-content .creditButtonStandalone p a {
    background-color: #FBFBFB;
    box-shadow: none;
    border-radius: 0;
    padding: 2px;
    border: solid 1px #A2A9B1;
    color: #202122;}#page-content .creditButtonStandalone p a:hover {
    background-color: #DDDDFF;
    color: #FBFBFB;}
 #page-content .modalbox h2 { border-bottom-width: 0px; }#page-content .modalbox {
    border: solid 1px #A2A9B1;
    border-radius: 0;
    box-shadow: none;}

/* ---- PAGE RATING ---- */
 .page-rate-widget-box {
    box-shadow: none;
    margin: unset;
    margin-bottom: 4px;
    border-radius: 0;
    padding: 2px;
    border: solid 1px #A2A9B1;}
 .page-rate-widget-box .rate-points {
    background-color: #FBFBFB !important;
    color: #202122 !important;
    border: solid 1px #FBFBFB;
    border-radius: 0;}.page-rate-widget-box .rateup,.page-rate-widget-box .ratedown {
    background-color: #FBFBFB;
    border-top: solid 1px #FBFBFB;
    border-bottom: solid 1px #FBFBFB;}
 .page-rate-widget-box .rateup a,.page-rate-widget-box .ratedown a {
    background: transparent;
    color: #202122;}
 .page-rate-widget-box .rateup a:hover,.page-rate-widget-box .ratedown a:hover {
    background: #DDDDFF;}
 .page-rate-widget-box .cancel {
    background: transparent;
    background-color: #FBFBFB;
    border: solid 1px #FBFBFB;
    border-radius: 0;}
 .page-rate-widget-box .cancel a {
    color: #202122;}.page-rate-widget-box .cancel a:hover {
    background: #DDDDFF;
    border-radius: 0;}

