[[html]]

<!DOCTYPE html>
<html lang="zh">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>开！</title>
    <style>
        body {
            margin: 0;
            height: 100vh;
            background: #0a0a0a;
            font-family: 'Courier New', monospace;
            overflow: hidden;
        }

        .game-container {
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh
            gap: 30px;
            padding: 20px;
            opacity: 1;
        }



        canvas {
            border: 2px solid #999999;
            background: #000;
            box-shadow: 0 0 30px rgba(0, 255, 128, 0.2);
        }

        #scpDocument {
            width: 300px;
            height: 363px;
            background: rgba(0, 30, 15, 0.9);
            border: 1px solid #004422;
            color: #00ff88;
            padding: 20px;
            overflow-y: auto;
            line-height: 1.6;
        }

        #warning {
            position: fixed;
            opacity: 0.5;
            bottom: 100px; /* 调整位置 */
            color: #ff5555;
            animation: blink 1s infinite;
            display: none;
        }

        #breachEffect {
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: rgba(255,0,0,0.9);
            color: #fff;
            display: none;
            justify-content: center;
            align-items: center;
            flex-direction: column;
            z-index: 2000;
            font-family: 'Arial Black', sans-serif;
            text-align: center;
            animation: breachPulse 0.5s infinite;
        }

        @keyframes blink {
            50% { opacity: 0.3; }
        }

        @keyframes breachPulse {
            0% { background: rgba(200,0,0,0.9); }
            50% { background: rgba(255,0,0,0.9); }
            100% { background: rgba(200,0,0,0.9); }
        }

        .breach-text {
            font-size: 4em;
            text-shadow: 0 0 20px #fff;
            margin: 20px;
            transform: scale(3);
            animation: textAppear 2s forwards;
        }

        @keyframes textAppear {
            to {
                transform: scale(1);
                opacity: 1;
            }
            from {
                transform: scale(3);
                opacity: 0;
            }
        }

        .terminal-text {
            font-family: 'Courier New', monospace;
            font-size: 1.2em;
            margin: 10px;
            opacity: 0;
            animation: terminalTyping 3s 2s forwards;
        }

        @keyframes terminalTyping {
            from {
                opacity: 0;
                letter-spacing: -10px;
            }
            to {
                opacity: 1;
                letter-spacing: normal;
            }
        }

        /* 按钮哈哈哈哈哈哈哈哈哈哈哈哈 */
        #controls {
            position: fixed;
            bottom: 0;
            left: 0;
            width: 100%;
            height: 100px;
            display: flex;
            justify-content: center;
            align-items: center;
            gap: 10px;
            opacity: 0.5;
            z-index: 1000;
        }

        .control-button {
            width: 60px;
            height: 60px;
            background: #004422;
            border: 2px solid #00ff88;
            color: #00ff88;
            font-size: 24px;
            display: flex;
            justify-content: center;
            align-items: center;
            cursor: pointer;
            user-select: none;
            opacity: 0.7;
            border-radius: 10px;
            box-shadow: 0 0 10px rgba(0, 255, 128, 0.5);
        }

        .control-button:active {
            background: #00ff88;
            color: #004422;
        }

       #pauseOverlay {
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: rgba(0, 0, 0, 0.9);
            color: #00ff88;
            display: none;
            justify-content: center;
            align-items: center;
            flex-direction: column;
            z-index: 3000;
            font-family: 'Courier New', monospace;
            text-align: center;
        }

        #pauseOverlay h2 {
            font-size: 2.5em;
            margin-bottom: 20px;
        }

        #pauseOverlay p {
            font-size: 1.2em;
            margin-bottom: 30px;
        }

        #resumeButton {
            padding: 10px 20px;
            background: #004422;
            border: 2px solid #00ff88;
            color: #00ff88;
            font-size: 1.2em;
            cursor: pointer;
            user-select: none;
            border-radius: 5px;
            transition: background 0.3s, color 0.3s;
        }

        #resumeButton:hover {
            background: #00ff88;
            color: #004422;
        }
    </style>
</head>
<body>
    <div class="particles" id="particles"></div>
    <div class="game-container">
        <canvas id="gameCanvas" width="400" height="400"></canvas>
        <div id="scpDocument"></div>
    </div>
    <div id="warning">.</div>
    <div id="breachEffect">
        <div class="breach-text">收容失效</div>
        <div class="terminal-text">[系统崩溃] 实体已突破第███层防火墙</div>
        <div class="terminal-text" style="animation-delay:3s">页面已崩溃</div>
        <div class="terminal-text" style="animation-delay:5s">虚拟环收容境蕌寴峔嶏岦崨嵾巁帀幉庰廔弢彍怷</div>
    </div>

    <!-- 暂停pause！ -->
    <div id="pauseOverlay">
        <h2>您收到了一条消息</h2>
        <p>主管 13:32</p>
        <p>上班在玩什么呢？</p>
        <button id="resumeButton">回复“滚”</button>
    </div>

    <!-- 按钮，可以按 -->
    <div id="controls">
        <div class="control-button" id="up">⬆️</div>
        <div class="control-button" id="down">⬇️</div>
     <div class="control-button" id="left">⬅️</div>
        <div class="control-button" id="right">➡️</div>
    </div>

    <script>
        const canvas = document.getElementById('gameCanvas');
        const ctx = canvas.getContext('2d');
        const scpDocument = document.getElementById('scpDocument');
        const gameContainer = document.querySelector('.game-container');
        const warningElement = document.getElementById('warning');
        const breachEffect = document.getElementById('breachEffect');
        const particlesContainer = document.getElementById('particles');
        const pauseOverlay = document.getElementById('pauseOverlay');
        const resumeButton = document.getElementById('resumeButton');

        const GRID_SIZE = 20;
        const TILE_COUNT = canvas.width / GRID_SIZE;
        let snake = [{ x: 10, y: 10 }];
        let food = { x: 15, y: 15 };
        let direction = { x: 0, y: 0 };
        let nextDirection = { x: 1, y: 0 };
        let gameSpeed = 150;
        let score = 0;
        let phase = 0;
        let gameActive = true;

        const scpData = [
            "SCP-CN-3620 ",
            "正在重启中......",
            "实时数据吞吐量: [0] TB",
            "当前投喂文件数: [0]",
            "Dr.grape失败",
            "Resty失败",
            "剩余1人存活",
            "…………",
            "<span style='color:#ff5555'>坚持住！</span>"
        ];

        function createParticles() {
            for (let i = 0; i < 100; i++) {
                const particle = document.createElement('div');
                particle.className = 'particle';
                particle.style.left = `${Math.random() * 100}vw`;
                particle.style.top = `${Math.random() * 100}vh`;
                particle.style.animationDuration = `${Math.random() * 5 + 5}s`;
                particlesContainer.appendChild(particle);
            }
        }

        function gameLoop() {
            if (!gameActive) return;

            direction = nextDirection;
            const newHead = {
                x: (snake[0].x + direction.x + TILE_COUNT) % TILE_COUNT,
                y: (snake[0].y + direction.y + TILE_COUNT) % TILE_COUNT
            };

            if (snake.some(segment => segment.x === newHead.x && segment.y === newHead.y)) {
                gameOver();
                return;
            }

            snake.unshift(newHead);

            if (newHead.x === food.x && newHead.y === food.y) {
                score++;
                updateDocument();
                spawnFood();
                checkPhase();
                checkScoreForPause(); // 检查是否需要暂停，OKOK
            } else {
                snake.pop();
            }

            drawGame();
            setTimeout(gameLoop, gameSpeed);
        }

        function drawGame() {
            ctx.fillStyle = '#001100';
            ctx.fillRect(0, 0, canvas.width, canvas.height);

            snake.forEach((segment, index) => {
                ctx.fillStyle = `hsl(${120 + index * 5}, 70%, ${50 - index % 15}%)`;
                ctx.fillRect(
                    segment.x * GRID_SIZE,
                    segment.y * GRID_SIZE,
                    GRID_SIZE - 1,
                    GRID_SIZE - 1
                );
                ctx.strokeStyle = '#00ff88';
                ctx.strokeRect(
                    segment.x * GRID_SIZE,
                    segment.y * GRID_SIZE,
                    GRID_SIZE - 1,
                    GRID_SIZE - 1
                );
            });

            ctx.fillStyle = '#ff2255';
            ctx.beginPath();
            ctx.arc(
                food.x * GRID_SIZE + GRID_SIZE/2,
                food.y * GRID_SIZE + GRID_SIZE/2,
                GRID_SIZE/2 - 2, 0, Math.PI * 2
            );
            ctx.fill();
            ctx.strokeStyle = '#ff8888';
            ctx.lineWidth = 2;
            ctx.stroke();
        }

        function spawnFood() {
            let attempts = 0;
            let foundValidPosition = false;

            while (!foundValidPosition && attempts < 100) {
                food.x = Math.floor(Math.random() * 13) + 3;
                food.y = Math.floor(Math.random() * TILE_COUNT);

                foundValidPosition = !snake.some(segment => 
                    Math.abs(segment.x - food.x) < 2 && 
                    Math.abs(segment.y - food.y) < 2
                );

                attempts++;
            }

            if (!foundValidPosition) {
                food.x = (snake[0].x + 5) % TILE_COUNT;
                food.y = (snake[0].y + 5) % TILE_COUNT;
            }
        }

        function updateDocument() {
            scpData[2] = `实时数据吞吐量: [${score * 157}] TB`;
            scpData[3] = `当前投喂文件数: [${score}]`;

            const content = scpData
                .slice(0, Math.min(score + 2, scpData.length))
                .map(line => line.replace(/\[0\]/g, score))
                .map(line => `<p>${line}</p>`)
                .join('');

            scpDocument.innerHTML = content;
            scpDocument.scrollTop = scpDocument.scrollHeight;
        }
//这里不能动
        function checkPhase() {
            if (score % 7 === 0 && score > 0) {
                phase++;
                gameSpeed = Math.max(50, 150 - phase * 20);
                warningElement.style.display = 'block';
                setTimeout(() => warningElement.style.display = 'none', 2000);
            }
        }

        function checkScoreForPause() {
            if (score  === 12 && score > 0) {
                pauseGame();
            }
        }

        function pauseGame() {
            gameActive = false;
            pauseOverlay.style.display = 'flex';
        }

        function resumeGame() {
            gameActive = true;
            pauseOverlay.style.display = 'none';
            gameLoop(); // 继续游戏循环
        }

        function gameOver() {
            gameActive = false;
            breachEffect.style.display = 'flex';
            canvas.style.filter = 'blur(5px)';
            document.removeEventListener('keydown', handleKeyPress);





//难
            // 彩蛋：好样的
            if (score === 4) {
                const eggText = document.createElement('div');
                eggText.className = 'terminal-text';
                eggText.textContent = '👍 差点打败 KC';
                eggText.style.animation = 'terminalTyping 3s forwards';
                breachEffect.appendChild(eggText);
            }
          // 彩蛋：人如鸡
            if (score === 3) {
                const eggText = document.createElement('div');
                eggText.className = 'terminal-text';
                eggText.textContent = '仁济😓';
                eggText.style.animation = 'terminalTyping 3s forwards';
                breachEffect.appendChild(eggText);
            }
          // 彩蛋：神
            if (score > 19) {
                const eggText = document.createElement('div');
                eggText.className = 'terminal-text';
                eggText.textContent = '👍';
                eggText.style.animation = 'terminalTyping 3s forwards';
                breachEffect.appendChild(eggText);
            }
 // 彩蛋：神
            if (score < 20) {
                const eggText = document.createElement('div');
                eggText.className = 'terminal-text';
                eggText.textContent = '发配到D级餐厅洗碗😭';
                eggText.style.animation = 'terminalTyping 3s forwards';
                breachEffect.appendChild(eggText);
            }





            setTimeout(() => {
                const newText = document.createElement('div');
                newText.className = 'terminal-text';
                newText.textContent = `最佳得分：${score}`;
                newText.style.animation = 'terminalTyping 3s forwards';
                breachEffect.appendChild(newText);
            }, 7000);

            document.body.addEventListener('click', () => {
                document.documentElement.requestFullscreen().catch(() => {});
            }, { once: true });
        }

        function handleKeyPress(e) {
            e.preventDefault();
            switch(e.key) {
                case 'ArrowLeft':
                    if (direction.x === 0) nextDirection = { x: -1, y: 0 };
                    break;

                case 'ArrowUp':
                    if (direction.y === 0) nextDirection = { x: 0, y: -1 };
                    break;
                case 'ArrowDown':
                    if (direction.y === 0) nextDirection = { x: 0, y: 1 };
                    break;
                case 'ArrowRight':
                    if (direction.x === 0) nextDirection = { x: 1, y: 0 };
                    break;
            }
        }

        // 绑定虚拟按钮事件
        document.getElementById('left').addEventListener('click', () => {
            if (direction.x === 0) nextDirection = { x: -1, y: 0 };
        });
        document.getElementById('up').addEventListener('click', () => {
            if (direction.y === 0) nextDirection = { x: 0, y: -1 };
        });
        document.getElementById('down').addEventListener('click', () => {
            if (direction.y === 0) nextDirection = { x: 0, y: 1 };
        });
        document.getElementById('right').addEventListener('click', () => {
            if (direction.x === 0) nextDirection = { x: 1, y: 0 };
        });

        // 绑定继续游戏按钮事件，这里绑上面
        resumeButton.addEventListener('click', resumeGame);

        function initGame() {
            gameContainer.style.display = 'flex';
            document.addEventListener('keydown', handleKeyPress);
            spawnFood();
            updateDocument();
            drawGame();
            gameLoop();
            createParticles();
        }

        initGame();
    </script>
</body>
</html>

[[/html]]












