        const pauseOverlay = document.getElementById('pauseOverlay');
        const resumeButton = document.getElementById('resumeButton');

        const GRID_SIZE = 20;
        const TILE_COUNT = canvas.width / GRID_SIZE;
        let snake = [{ x: 10, y: 10 }];
        let food = { x: 15, y: 15 };
        let direction = { x: 0, y: 0 };
        let nextDirection = { x: 1, y: 0 };
        let gameSpeed = 150;
        let score = 0;
        let phase = 0;
        let gameActive = true;

        const scpData = [
            "SCP-CN-3620 ",
            "æ­£åœ¨é‡å¯ä¸­......",
            "å®æ—¶æ•°æ®ååé‡: [0] TB",
            "å½“å‰æŠ•å–‚æ–‡ä»¶æ•°: [0]",
            "Dr.grapeå¤±è´¥",
