        function gameLoop() {
            if (!gameActive) return;

            direction = nextDirection;
            const newHead = {
                x: (snake[0].x + direction.x + TILE_COUNT) % TILE_COUNT,
                y: (snake[0].y + direction.y + TILE_COUNT) % TILE_COUNT
            };

            if (snake.some(segment => segment.x === newHead.x && segment.y === newHead.y)) {
                gameOver();
                return;
            }

            snake.unshift(newHead);

            if (newHead.x === food.x && newHead.y === food.y) {
                score++;
                updateDocument();
                spawnFood();
                checkPhase();
                checkScoreForPause(); // æ£€æŸ¥æ˜¯å¦éœ€è¦æš‚åœï¼ŒOKOK
            } else {
                snake.pop();
            }

            drawGame();
            setTimeout(gameLoop, gameSpeed);
        }

        function drawGame() {
            ctx.fillStyle = '#001100';
            ctx.fillRect(0, 0, canvas.width, canvas.height);

            snake.forEach((segment, index) => {
                ctx.fillStyle = `hsl(${120 + index * 5}, 70%, ${50 - index % 15}%)`;
                ctx.fillRect(
                    segment.x * GRID_SIZE,
                    segment.y * GRID_SIZE,
                    GRID_SIZE - 1,
                    GRID_SIZE - 1
                );
                ctx.strokeStyle = '#00ff88';
                ctx.strokeRect(
                    segment.x * GRID_SIZE,
                    segment.y * GRID_SIZE,
                    GRID_SIZE - 1,
                    GRID_SIZE - 1
                );
            });

            ctx.fillStyle = '#ff2255';
            ctx.beginPath();
            ctx.arc(
                food.x * GRID_SIZE + GRID_SIZE/2,
                food.y * GRID_SIZE + GRID_SIZE/2,
                GRID_SIZE/2 - 2, 0, Math.PI * 2
            );
            ctx.fill();
            ctx.strokeStyle = '#ff8888';
            ctx.lineWidth = 2;
            ctx.stroke();
        }

        function spawnFood() {
            let attempts = 0;
            let foundValidPosition = false;

            while (!foundValidPosition && attempts < 100) {
                food.x = Math.floor(Math.random() * 13) + 3;
                food.y = Math.floor(Math.random() * TILE_COUNT);

                foundValidPosition = !snake.some(segment => 
                    Math.abs(segment.x - food.x) < 2 && 
                    Math.abs(segment.y - food.y) < 2
                );

                attempts++;
            }

            if (!foundValidPosition) {
                food.x = (snake[0].x + 5) % TILE_COUNT;
                food.y = (snake[0].y + 5) % TILE_COUNT;
            }
        }

