:root{
    --basalt-bright-element-color: 20, 145, 168;
    --basalt-dark-element-color: 18, 110, 130;
    --basalt-darker-element-color: 19, 72, 87;

    --basalt-undertone: 19, 72, 87;
    --basalt-main-text-color: 87.8% 89.13% 87.85%;
}

@supports (font-family: fangsong) {
  div.blockquote em {
    font-style: normal;
    font-weight: bold;
    font-family: fangsong;
  }
}

/* ACS color */

.anom-bar-container.ekhi .text-part .disrupt-class::before {
  color: white;
}

.anom-bar-container.caution .text-part .risk-class::before {
  color: white;
}

/* FloatBox from basalt */

#page-content div.floatbox {
    background-color: rgb(var(--basalt-tertiary-color));
    float: left;
    max-width: calc(var(--main-content-width)/1.8);
    box-sizing: border-box;
    margin: 0.25rem 1.25rem;
    margin-left: clamp(calc(var(--main-content-width)/-4.5), calc((87.5vw - 95%)/-2), 0rem);
    padding-inline: 1.2rem;
    padding-block: 0.5rem;
    font-size: 0.9125em;
    border-radius: 7px;
    border: 3px solid rgba(var(--general-border-color), 0.4);
}

#page-content div.floatbox.right {
    float: right;
    margin-left: 1.25rem;
    margin-right: clamp(calc(var(--main-content-width)/-4.5), calc((87.5vw - 95%)/-2), 0rem);
}

@media only screen and (max-width: 640px) {
    #page-content div:is(.floatbox, .floatbox.right) {
     float: none;
     margin: 0.5rem auto;
     width: 100%;
    }
}

/* anchor */

a:link, a:visited {
    color: inherit;
    text-decoration: currentcolor solid underline;
}
a:hover, a:active {
    background-color: #FFF;
    color: #000;
    text-decoration: #111 solid underline;
}

.earthworm a:link, .earthworm a:visited {
    color: #fffef9 !important;
    text-decoration: currentcolor solid underline !important;
}

.earthworm a:hover, .earthworm a:active {
    color: #000107 !important;
    text-decoration: #111 solid underline !important;
}

/* other items */

#page-content hr {
    background-color: white;
}

h1, h2, h3, h4, h5, h6 {
  text-align: center;
  margin-top: 12px;
  margin-bottom: 12px; 
}

h5 {
  margin-left: 25%;
}

span.disnone{
  font-weight: bold;
  color: #9f9f9f;
  background: linear-gradient(to bottom, #9f9f9fff, #9f9f9fb0 50%, #9f9f9f40 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}


span.youra{
  font-weight: bold;
  color: #856d72;
  background: linear-gradient(to bottom, #856d72ff, #856d72b0 50%, #856d7240 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}

.nogap p {margin: 0;}

.gridl {
  display: grid; 
  grid-template-columns: 28% 1fr;
  align-items: center; 
