}

.e-blockquote {
  position:relative;
  padding-top: 0px;
  padding-bottom: 0px;
  margin-bottom: 5px;
  border:solid #707070;  
  border-width:0px 0px 0px 0px;
}

.e-blockquote2 {
  position:relative;
  padding-top: 0px;
  padding-bottom: 1px;
  margin-bottom: 0px;
  border:solid #707070;  
