[[module CSS]]
.email {
    position: relative;
    border: solid 2px #ADD8E6;
    width: 88%;
    padding: 10px 15px;
    margin: 10px;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.5);
    background: linear-gradient(rgba(0, 0, 0, 1), rgba(70, 70, 70, 1));
    background-size: cover;
    background-position: center top;
    opacity: 0.9;
    color: #FFFFFF;
    font-family: 'Arial', sans-serif;
