    overflow: hidden;
}

.email::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: transparent;
    z-index: -1;
}

.email-example a.collapsible-block-link {
    font-weight: bold;
    color: #FFFFFF;
    text-decoration: none;
    position: relative;
    animation: blink 1s ease-in-out infinite alternate;
}

.tofrom {
    margin-left: 10px;
    margin-top: 5px;
    padding: 5px 15px;
    border-left: solid 3px #ADD8E6;
    font-style: italic;
    background-color: rgba(173, 216, 230, 0.1);
    color: #FFFFFF;
}

.email:hover {
    box-shadow: 0 8px 16px rgba(0, 0, 0, 0.6);
    transition: box-shadow 0.3s ease-in-out;
}

@keyframes blink {
    0%, 50% { color: #FFFFFF; }
    100% { color: #CC4400; } 
}
