[[module css]]
div#page-content div.earthworm div.first:not(.true),
div#page-content div.earthworm div.last:not(.true) {
    border: none;
    background-color: #070D29;
}

div#page-content div.earthworm div.first:not(.true) img,
div#page-content div.earthworm div.last:not(.true) img {
    display: none;
}

div#page-content div.earthworm div.first:not(.true) {
    margin-left: 1.5rem;
}

div#page-content div.earthworm div.first:not(.true)::before {
    content: "";
    display: block;
    position: absolute;
    height: 100%;
    width: 1.5rem;
    left: -1.5rem;
    top: 0;
    margin: 0;
    background-color: #070D29;
    clip-path: polygon(0% 50%, 100% 0%, 100% 100%);
    -webkit-clip-path: polygon(0% 50%, 100% 0%, 100% 100%);
}

div#page-content div.earthworm div.last:not(.true) {
    margin-right: 1.5rem;
}

div#page-content div.earthworm div.last:not(.true)::before {
    content: "";
    display: block;
    position: absolute;
    height: 100%;
    width: 1.5rem;
    right: -1.5rem;
    top: 0;
    margin: 0;
    background-color: #070D29;
    clip-path: polygon(100% 50%, 0% 0%, 0% 100%);
    -webkit-clip-path: polygon(100% 50%, 0% 0%, 0% 100%);
}

div#page-content div.earthworm div.hub {
    border: 3px solid #000107;
    background-color: #070D29;
}

/*上面的代码修改自Dr. Hormress的平行版式。*/

.hub.yes a{
color: #ffffff;
background: linear-gradient(to bottom, #ffffffb0, #ffffff80 50%, #ffffff00 100%);
-webkit-background-clip: text;
-webkit-text-fill-color: transparent;
}
.last.false a{color: #fffef9;}

.earthworm a{font-weight: bold !important;}
[[/module]]
