<script>
        var result = '10:00'
        var timeOut = 60
        var timeNoSignal = 0
        var minute = 0
        var second = 10
        var mic = 0
        window.document.onmousemove = function () {
            console.log('操作を検出')
            timeNoSignal = 0
            minute = 0
            second = 10
            mic = 0
            clearInterval(counter)
        }
        window.document.ontouchmove= function () {
            console.log('携帯端末の操作を検出')
            timeNoSignal = 0
            minute = 0
            second = 10
            mic = 0
            clearInterval(counter)
        }
        function counting() {
            console.log("カウントダウン ")
            // setTimeout(function(){document.getElementById("counter").style.display = 'flex'},1000)
	    // document.getElementById("counter").style.cssText='display: -webkit-box; display: -ms-flexbox; display: flex; '
            document.getElementById("counter").style.display = 'flex'
            // setTimeout(function(){
            var counter
            clearInterval(counter)
            counter = setInterval(function () {
                console.log(minute + ' ' + second + ' ' + mic)
                if (minute <= 0 && second <= 0) {
                    console.log('終了')
                    document.getElementById("countNum").innerHTML = '00:00:00'
                    document.getElementById("text").innerHTML = '資産不活化、物質682-Dを投入開始。</br>サイト内セキュリティへ代替資産の護送を通達済。'
                    //document.getElementById("keepReading").style.display='none'
                    document.getElementById('keepReading').style.animation = 'close 0.5s forwards'
                    clearInterval(counter)
                    return
                }
                if (mic == 0) {
                    console.log(result)
                    mic = 99
                    if (second == 0) {
                        second = 59
                        minute--
                    }
                    else
                        second--
                }
                else
                    mic--
                var Minute = minute.toString()
                var Second = second.toString()
                var Mic = mic.toString()
                if (Minute.length < 2)
                    Minute = '0' + minute
                if (Second.length < 2)
                    Second = '0' + second
                if (Mic.length < 2)
                    Mic = '0' + mic
                result = Minute + ':' + Second + ':' + Mic
                document.getElementById("countNum").innerHTML = result
            }, 10)
            // },2000)
        }
        // counting()
        function listener() {
            clearInterval(listening)
            var listening = setInterval(function () {
                if (timeNoSignal >= timeOut) {
                    counting()
                    clearInterval(listening)
                }
                else {
                    console.log('操作観測されず:' + timeNoSignal)
                    timeNoSignal++
                }

            }, 1000)
        }
        listener()
        function read() {
            //http://scp-jp.wikidot.com/random:random-redirect/tags/%2Bscp+-%E5%85%83%E8%A8%98%E4%BA%8B%E5%89%8A%E9%99%A4+-%E3%82%A2%E3%83%BC%E3%82%AB%E3%82%A4%E3%83%96%E6%B8%88%E3%81%BF
            console.log("閲覧を続ける")
            window.open("http://scp-jp.wikidot.com/random:random-redirect/tags/%2Bscp+-%E5%85%83%E8%A8%98%E4%BA%8B%E5%89%8A%E9%99%A4+-%E3%82%A2%E3%83%BC%E3%82%AB%E3%82%A4%E3%83%96%E6%B8%88%E3%81%BF");
        }
	function Alert(info){

            document.getElementById("footNoteContent").innerHTML = info

	    // document.getElementById("footNote").style.cssText='display: -webkit-box; display: -ms-flexbox; display: flex;'
            document.getElementById("footNote").style.display = 'flex'

        }
    </script>
    <style>
