[[include :scp-wiki-cn:theme:parallel]]

[[module css]]
:root{
    --basalt-bright-element-color: 93, 153, 101;
    --basalt-dark-element-color: 57, 117, 67;
    --basalt-darker-element-color: 0, 52, 16;

    --basalt-undertone: 0, 52, 16;
    --basalt-main-text-color: 0, 15, 1;
    --basalt-primary-color: 231, 229, 228;
}

/* anchor */

a:link, a:visited {
    color: inherit;
    text-decoration: currentcolor solid underline;
}
a:hover, a:active {
    background-color: #FFF;
    color: #000;
    text-decoration: #111 solid underline;
}

/* other items */

#page-content hr {
    background-color: rgb(var(--basalt-main-text-color)) !important;
}

h1, h2, h3, h4, h5, h6 { text-align: center; }

li:not(:first-child) {margin-top: 0.75em;}

ul, ol {padding-left: 10px;}

em {
  font-style: normal;
  font-family: "KaiTi", "楷体", serif;
}

span.stay-italic em {
  font-style: italic;
  font-family: revert;
}

/* ruby fix from scp-cn-2512 */

p:has(span.ruby) {
    line-height: 1.7;
}

span.ruby {
    height: unset;
    display: inline-flex;
    flex-direction: column-reverse;
    align-items: center;
    vertical-align: top;
}

span.rt {
    display: block;
    margin-top: -0.625em;
    margin-bottom: 0;
    line-height: 1.25;
}

/* specified modification */
#page-title {display: none;}

.footnotes-footer {display: none;}

.error-block {display: none;}

/* word document */

.word-document {
  --side-width: 5rem;
  --top-height: 5rem;
  --locator-length: 1.5rem;
  --locate-color: #aaa;
  --h-f-indent: 0.5em;
  border: 2px solid black;
  border-width: 6px 2px;
  background-color: #ffffff;
  width: calc(var(--main-content-width)*0.925);
  margin: 2rem auto;
}

.word-document .wheader, .word-document .wfooter {
  position: relative;
  height: var(--top-height);
  margin: 0 var(--side-width);
  text-align: center;
  color: #aaa;
  font-size: 80%;
}

.wheader p {
  position: absolute;
  bottom: var(--h-f-indent);
  margin: 0;
  width: 100%;
  text-align: center;
}

.wfooter p:first-of-type {
  margin-top: var(--h-f-indent);
}

.wheader::before {
  content: " ";
  position: absolute;
  width: var(--side-width);
  height: var(--top-height);
  left: calc(0px - var(--side-width));
  top: 0;
  background:
    linear-gradient(var(--locate-color), var(--locate-color)) right bottom,
    linear-gradient(var(--locate-color), var(--locate-color)) right bottom;
  background-position: right bottom, right bottom;
  background-repeat: no-repeat;
  background-size: 1px var(--locator-length), var(--locator-length) 1px;
}

.wheader::after {
  content: " ";
  position: absolute;
  width: var(--side-width);
  height: var(--top-height);
  left: 100%;
  top: 0;
  background:
    linear-gradient(var(--locate-color), var(--locate-color)) left bottom,
    linear-gradient(var(--locate-color), var(--locate-color)) left bottom;
  background-position: left bottom, left bottom;
  background-repeat: no-repeat;
  background-size: 1px var(--locator-length), var(--locator-length) 1px;
}

.word-document .word-content {
  margin: 0 var(--side-width);
  line-height: 1.5;
}

.wfooter::before {
  content: " ";
  position: absolute;
  width: var(--side-width);
  height: var(--top-height);
  left: calc(0px - var(--side-width));
  top: 0;
  background:
    linear-gradient(var(--locate-color), var(--locate-color)) right top,
    linear-gradient(var(--locate-color), var(--locate-color)) right top;
  background-position: right top, right top;
  background-repeat: no-repeat;
  background-size: 1px var(--locator-length), var(--locator-length) 1px;
}

.wfooter::after {
  content: " ";
  position: absolute;
  width: var(--side-width);
  height: var(--top-height);
  left: 100%;
  top: 0;
  background:
    linear-gradient(var(--locate-color), var(--locate-color)) left top,
    linear-gradient(var(--locate-color), var(--locate-color)) left top;
  background-position: left top, left top;
  background-repeat: no-repeat;
  background-size: 1px var(--locator-length), var(--locator-length) 1px;
}

.word-document hr {
  height: 0.1rem;
}

@media (max-width: 1024px) {
  .word-document {
    --side-width: 2.5rem;
    --top-height: 2.5rem;
    --locator-length: 1rem;
  }
  .wheader p {bottom: 0;}
}

/* those inside documents */

.word-content .scp-image-block {
  border: none;
  background-color: transparent;
}

.word-content .scp-image-caption {
  background-color: transparent;
  border: none;
  font-family: "SimSun", "宋体", "Times New Roman", fangsong, serif;
  font-weight: bold;
}

.t-g-title {
  font-size: 90%;
  font-family: "SimSun", "宋体", "Times New Roman", fangsong, serif;
  font-weight: bold;
}

.word-content .three-lines {
  font-size: 90%;
}

.word-content .blockquote,  .word-content blockquote {
  background-color: #bbb4;
  box-shadow: none;
}

:is(.blockquote, blockquote) :is(.blockquote, blockquote) {
  background-color: transparent;
  border-left: 1px solid black;
  border-right: 1px solid black;
  border-top: 4px double black;
  border-bottom: 2.5px solid black;
}

@media (max-width: 1024px) {
  .word-content .blockquote,  .word-content blockquote { padding-inline: 0.5rem;}
  :is(.blockquote, blockquote) :is(.blockquote, blockquote) { padding-inline: 0.5rem;}
}

a.collapsible-block-link {
  color: transparent;
  font-size: 0;
  text-decoration: none !important;
}

a.collapsible-block-link::after {
  color: black;
  font-size: 1rem;
  font-weight: bold;
}

div.collapsible-block-folded a.collapsible-block-link::after {
  content: "[+]参考文献";
}

div.collapsible-block-unfolded a.collapsible-block-link::after {
  content: "[-]参考文献";
}

/* other components */

.article-title {
  display: grid;
  grid-template-columns: 1fr 4fr;
  width: 80%;
  margin: 4rem auto 4rem auto;
  align-items: center;
}

.site-code {
  font-size: 140%;
  letter-spacing: 0.4em;
}

.text-title {
  font-weight: bold;
  font-size: 320%;
  letter-spacing: 0.05em;
  text-align: center;
}

.fillin-grid {
  width: 80%;
  display: grid;
  grid-template-columns: 2fr 5fr;
  font-size: 135%;
  margin: 0 auto;
}

.fillin {
  border-bottom: 1px solid black;
  text-align: center;
}

.fillin-grid p {
  margin-bottom: 0;
}

@media (max-width: 1024px) {
  .article-title {
    grid-template-columns: 1fr;
    width: 100%;
  }
  .article-title img {max-width: 40vw;}
  .site-code {letter-spacing: 0;}
  .text-title {
    font-size: 200%;
    letter-spacing: 0;
  }
  .fillin-grid {
    width: 95%;
    font-size: 110%;
  }
}

/* mail format */

div.mail-main {
    display: flex;
    flex-direction: column;
    gap: 1em;
    background-color: rgb(255, 255, 255);
    border: #000 solid;
    border-width: 0.375rem 0.125rem;
    margin: 2rem auto;
    padding: 1em;
    max-width: calc(var(--main-content-width) * 0.925);
}

div.mail-header {
    display: flex;
    justify-content: space-between;
    gap: 1em;
}

div.mail-header div.mail-info {
    display: grid;
    grid-template-columns: 3rem 1fr;
    grid-template-rows: 100%;
    gap: 1ch;
    align-items: center;
}

div.mail-header div.logo {
    background-color: rgb(var(--basalt-bright-element-color));
    background-image: url(https://scpsandboxcn.wdfiles.com/local--files/sharia-vanilla-others/site-cn-53.png);
    background-position: center;
    background-repeat: no-repeat;
    background-size: 2.5rem;
    border-radius: 50%;
    width: 3rem;
    height: 3rem;
}

div.mail-header div.mail-from-to {
    display: flex;
    flex-basis: auto;
    flex-direction: column;
    justify-content: space-between;
    height: 100%;
}

#page-content div.mail-header div.mail-options {
    display: flex;
    flex-wrap: wrap;
    justify-content: end;
    align-items: center;
    max-width: 10.5rem;
}

div.mail-header div.mail-options div.icon {
    flex-grow: 1;
    position: relative;
    width: 2.625rem;
    height: 2.25rem;
    box-sizing: border-box;
    transition: background-color 200ms linear;
}

div.mail-header div.mail-options div[data-icon="1"] {
    --icon: url("https://scpsandboxcn.wdfiles.com/local--files/collab:castle-tracer/replyicon.svg");
}

div.mail-header div.mail-options div[data-icon="2"] {
    --icon: url("https://scpsandboxcn.wdfiles.com/local--files/collab:castle-tracer/replyallicon.svg");
}

div.mail-header div.mail-options div[data-icon="3"] {
    --icon: url("https://scpsandboxcn.wdfiles.com/local--files/collab:castle-tracer/retransicon.svg");
}

div.mail-header div.mail-options div[data-icon="4"] {
    --icon: url("https://scpsandboxcn.wdfiles.com/local--files/collab:castle-tracer/moreicon.svg");
    border-left: 0.05rem rgb(237, 237, 237) solid;
}

div.mail-header div.mail-options div.icon:hover {
    background-color: rgb(239, 236, 213, 0.5);
}

div.mail-header div.mail-options div.icon::before {
    content: "";
    background-image: var(--icon);
    background-position: center;
    background-repeat: no-repeat;
    background-size: 1.325rem;
    position: absolute;
    display: block;
    width: 100%;
    height: 100%;
    inset: 0;
}

div.mail-header div.mail-options div.icon span {
    position: absolute;
    top: -3em;
    left: -50%;
    width: 200%;
    opacity: 0;
    pointer-events: none;
    background-color: rgb(255, 255, 255);
    box-shadow: 0 3px 3px 1px rgb(170, 170, 170, 50%);
    padding: 0.75ch 0;
    text-align: center;
    transition: opacity 100ms linear 100ms;
}

div.mail-header div.mail-options div.icon:hover :where(span, span:hover) {
    opacity: 1;
    pointer-events: auto;
}

div.mail-header div.mail-options div.date {
    font-size: 0.8em;
}

div.mail-content {
    border-bottom: 0.1rem rgb(var(--basalt-main-text-color)) solid;
    margin-inline: 3.25rem;
}

div.mail-content div.sign {
    text-align: right;
}

div.mail-file {
    margin: 1rem 3.25rem 2rem 3.25rem;
}

div.mail-file a {
    margin-inline: 1ch;
}

div.mail-file a::before {
    content: "";
    display: inline-block;
    background-image: url("https://scpsandboxcn.wdfiles.com/local--files/collab:castle-tracer/wordicon.svg");
    background-position: center;
    background-repeat: no-repeat;
    background-size: contain;
    margin-right: 1em;
    width: 1em;
    height: 1em;
    transform: scale(2);
}

@media (max-width: 64rem) {
    div.mail-content,
    div.mail-file {
        margin: 0;
    }
}

@media (max-width: 35rem) {
    div.mail-header {
        flex-direction: column;
    }
    #page-content div.mail-header div.mail-options {
        max-width: none;
    }
    div.mail-header div.mail-options div[data-icon] {
        flex-grow: 0;
    }
    div.icon:not([data-icon="1"], [data-icon="4"]) {
        display: none;
    }
    div.mail-header div.date {
        order: -1;
        flex-grow: 1;
    }
}

