[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+SC:wght@300;400;500;700;900');
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+SC:wght@300;400;500;700;900');

:root {
    --sp_header-logo: url("https://ah-sandbox.wikidot.com/local--files/theme:miku/logomiku2.svg");
    --header-title: url("https://ah-sandbox.wikidot.com/local--files/theme:miku/miku3.svg");

    --sp_header-gradient-top-color: #ECECEC;
    --sp_header-gradient-bottom-color: var(--sp_header-gradient-top-color);
    --sp_top-bar-gradient-top-color: #393939;
    --sp_top-bar-gradient-bottom-color: var(--sp_top-bar-gradient-top-color);
    --sp_diagonal-stripes-color: #39393910;

    --sp_diagonal-stripes-gap: 1rem;
    --sp_header-height: 10rem;
    --sp_top-bar-height: 2rem;

    --sp_dropdown-background: #ECECEC;
    --sp_top-bar-link-hover-color: #393939;

    --sp_rate-module-background: #393939;


    --main-content-width: 50rem;
}

body {
    font-family: "Noto Sans SC", sans-serif;
    font-size: 0.925rem;
    background-color: #ECECEC;
}

div#header {
    background-image: repeating-linear-gradient(var(--sp_diagonal-stripes-angle), var(--sp_diagonal-stripes-color), var(--sp_diagonal-stripes-color) var(--sp_diagonal-stripes-width), transparent var(--sp_diagonal-stripes-width), transparent var(--sp_diagonal-stripes-gap)),
    repeating-linear-gradient(calc(-1 * var(--sp_diagonal-stripes-angle)), var(--sp_diagonal-stripes-color), var(--sp_diagonal-stripes-color) var(--sp_diagonal-stripes-width), transparent var(--sp_diagonal-stripes-width), transparent var(--sp_diagonal-stripes-gap)),
    linear-gradient(to bottom, var(--sp_header-gradient-top-color) calc(var(--sp_header-gradient-top-stop) * var(--sp_header-height)), var(--sp_header-gradient-bottom-color) calc(var(--sp_header-gradient-bottom-stop) * var(--sp_header-height)));
}

div#header::before {
    z-index: 2;
}

div#header h1 {
    position: relative;
    width: max-content;
    grid-row: 2/6;
}

div#header h1 a {
    display: flex;
    height: 100%;
    position: relative;
    z-index: 2;
}

div#header h1 a::before {
    display: block;
    align-self: center;
    min-width: min(20rem, 50vw);
}

div#header h2 {
    display: none;
}

input#search-top-box-input {
    display: none;
}

form#search-top-box-form input[type="submit"] {
    background-image: none;
    box-shadow: none;
    border: none;
    border-radius: 0.5rem;
    background-color: #393939;
    transition: ease 0.125s;
}

form#search-top-box-form input[type="submit"]:hover,
form#search-top-box-form input[type="submit"]:focus {
    background-image: none;
    box-shadow: none;
    border: none;
    background-color: #FF0094;
}

div#login-status {
    width: max-content;
    justify-self: end;
    background-color: #393939;
    padding: 0.2rem;
    border-radius: 0.5rem;
}

div#login-status a#account-topbutton {
    border: none;
}

div#account-options {
    border-radius: 0.5rem;
    border: solid 0.2rem #FF0094;
}

div#account-options ul {
    display: grid;
    text-align: center;
    grid-template-columns: 1fr 1fr;
}

div#header-extra-div-1 {
    position: relative;
    grid-column: 2 / 3;
    grid-row: 1 / 6;
    justify-self: end;
    width: 5rem;
    margin-right: 1rem;
    background-image: linear-gradient(to right, #666 2rem, #FF0094 2rem 3rem, #666 3rem);
    transform: skewX(-45deg);
}

div#header-extra-div-1::after {
    content: "";
    position: absolute;
    background-color: #666;
    width: 2rem;
    height: 0.2rem;
    right: 0rem;
    bottom: -0.2rem;
}

div#top-bar {
    border-block: solid 0.2rem #FF0094;
    outline: solid 0.2rem #666;
}

div#top-bar div[class*="top-bar"] > ul {
    justify-content: center;
}

div#top-bar div[class*="top-bar"] > ul li {
    flex-grow: 1;
}

div#top-bar div[class*="top-bar"] > ul > li > a {
    border-inline: solid 0.2rem #393939;
}

div#top-bar div[class*="top-bar"] > ul li * {
    transition: ease 0.125s;
}

div#top-bar :is(.top-bar, .mobile-top-bar) ul li ul {
    box-shadow: none;
    border: solid 0.2rem #FF0094;
    border-top: none;
}

div#side-bar div.side-block {
    border: solid 0.2rem #FF0094;
    border-radius: 0.5rem;
    box-shadow: none;
}

div#content-wrap {
    margin-top: 0.2rem;
    width: 100%;
    max-width: 100%;
}

div#main-content {
    max-width: 100%;
}

div#page-title {
    color: #ECECEC;
    font-weight: 900;
    font-size: 2.5rem;
    font-family: "Noto Sans SC", sans-serif;
    background-color: #00B0B0;
    background-image: url("https://ah-sandbox.wikidot.com/local--files/theme:miku/miku_title_back.svg");
    background-size: cover;
    min-height: 10rem;
    box-sizing: border-box;
    padding-block: 5rem 1rem;
    padding-inline: max(calc(50vw - var(--main-content-width) / 2 - 0.5rem), 1rem);
    margin-bottom: 0;
    line-height: 4rem;
}

div#page-title::before {
    content: "█ ";
}

div#page-content {
    max-width: min(var(--main-content-width), calc(100vw - 2rem));
    margin-inline: auto;
}

div#page-options-container,
div#action-area,
div#main-content div.page-tags {
    width: auto;
    padding-inline: max(calc(50vw - var(--main-content-width) / 2 - 0.5rem), 1rem);
}

div.page-rate-widget-box,
div.rate-box-with-credit-button {
    box-shadow: none;
    padding-block: 0;
    border-radius: 0.5rem;
}

div#page-content > div > div.page-rate-widget-box:nth-child(1),
div#page-content > div.creditRate {
    margin-top: -10.4px;
}

div.modalbox {
    border-width: 0.2rem;
    border-radius: 0.5rem;
    box-shadow: none;
}

div.basicblock {
    border: solid 0.2rem #666;
    border-radius: 0.5rem;
    background-color: #f4f4f4;
    padding: 0 1em;
    margin-block: 1.5em;
}

div.blockquote {
    margin: 1em 2em;
}

span.post-reply-number {
    border: 1px solid grey;
    color: grey;
    display: inline-block;
    padding: 0 4px;
    border-radius: 6px;
}

div#page-content hr {
    margin: 1em 0;
    border-top: 1px dashed grey;
    background: transparent;
}

span.broken-text {
    font-weight: bold;
    color: #00b0b0;
}

div.image-basic-block {
    margin: 1em 0;
}

div.image-basic-block img {
    width: 100%;
}
[[/module]]

