[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+SC:wght@300;400;700;900&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans:wght@300;400;700;900&display=swap');

div#page-content {
    color: #000;
    font-size: 0.9375rem;
    font-family: "Noto Sans SC", "Noto Sans", sans-serif;
}

div#page-content *,
div#page-content h1,
div#page-content h2,
div#page-content h3 {
    font-family: inherit;
}

div.warning-box {
    display: grid;
    gap: min(0.5rem, 2vw);
    grid-template-columns: 1fr 3fr;
}

div.warning-box div.warning-logo {
    display: flex;
    align-items: center;
}

div.warning-box div.warning-content {
    display: flex;
    align-items: center;
    /* border: solid min(0.25rem, 1vw) #000; */
    border-left: none;
    border-right: none;
    padding: min(0.5rem, 2vw) 0;
    text-align: center;
}

div.warning-box div.warning-content * {
    margin: 0;
}

div.warning-box div.warning-content h2 {
    font-weight: 900;
    font-size: min(calc(0.9375rem * 1.5), 4vw);
    margin-bottom: min(0.5rem, 2vw);
}

div.warning-box div.warning-content p {
    font-weight: 400;
    margin: 0 min(0.5rem, 2vw);
    font-size: min(0.9375rem, calc(8vw / 3));
}

div.blockquote.content {
    border: solid 1px transparent;
    background-color: #EEE;
    border-radius: 1rem;
    margin-left: 0;
    margin-right: 0;
}

hr {
    margin-left: 0;
    margin-right: 0;
}

span.black {
    background-color: #000;
    cursor-events: none;
}

div.scp-image-block div.scp-image-caption,
div.scp-image-block img {
    width: auto !important;
}
[[/module]]


[[module css]]
#page-content div.myBasicBlock {
    border-radius: 8px;
    border: solid 1px #615;
    background: #FAFAFA;
    -webkit-box-shadow: 0 1px 6px rgba(0,0,0,0.25);
            box-shadow: 0 1px 6px rgba(0,0,0,0.25);
    -webkit-box-sizing: border-box;
            box-sizing: border-box;
    width: 100%;
    padding: 15px;
    margin: 1em 0;
    display: inline-block;
}

#page-content div.myBasicBlock hr {
    margin-left: 0;
}

#page-content .colored {
    background-color: #e2e7bf !important;
}

#page-content span.redacted {
    color: #333;
    background-color: #333;
    -webkit-user-select: none;
            user-select: none;
}

.scp-image-block.block-right {
  margin: 0 0 1em 2em;
}

[[/module]]


