[[include :scp-wiki-cn:theme:bedrock]]

[[include :oxygennine:comp:moxdivs]]

[[module CSS]]

body {background-color: #101010;}

.artcon img.image {
    border: solid 4px #2b2b2b;
    margin: 2em auto;
}

:root {
    --basalt-primary-color: 28,28,28;
    --basalt-secondary-color: 10,10,10;
    --basalt-tertiary-color: 40,40,40;
    --basalt-main-text-color: 190,190,190;
    --basalt-overtone: 222,222,222;
    --basalt-undertone: 100,100,100;

    --basalt-bright-element-color: 200,200,200;
    --basalt-dark-element-color: 66,66,66;
    --basalt-alternate-color: 140,140,140;
    --basalt-cancel-color: var(--basalt-negative-color);
    --basalt-sub-text-color: 120,120,120;
    --basalt-light-text-color: var(--basalt-primary-color);

    --basalt-UI-dark-palette: var(--basalt-overtone);
    --basalt-focus-color: var(--basalt-undertone);
    --basalt-background-color: var(--basalt-primary-color);

    --link-color: 150,50,50;
    --link-visited-color:  120,50,50;
    --link-newpage-color:  180,50,50;

    --main-content-width: 65rem;
    --title-size: 2rem;
    --subtitle-size: 0.1rem;

}

:root:lang(cn) {
    --header-title: 'SCP档案';
    --header-subtitle: '';
}

:root:lang(cn) {
    --body-font-fallback: "Noto Sans SC", Verdana, "Microsoft YaHei", "微软雅黑", Geneva, sans-serif, STXihei, "华文细黑";
    --UI-font-fallback: "Noto Sans SC", "方体", "PingFang SC", "黑体", "Heiti SC", "Microsoft JhengHei UI", "Microsoft JhengHei", Roboto, Noto, "Noto Sans CJK SC", sans-serif;
    --header-font-fallback: var(--body-font-fallback);
    --mono-font-fallback: "Noto Serif SC", "Microsoft YaHei", "微软雅黑", "Courier New", Courier, monospace, STXihei, "华文细黑";
}

#header h1 a::before {
display:none;
}

div#page-title {
    display: none;
}

#header {
    box-shadow: 0px 0px 30px 10px #000000;
    border: none;
}

/* custom */

span.tight {
    margin: 0rem 0rem -2rem 0rem;
    display: inline-block;
}

.tid p {
    text-indent: 2em;
}

sup.footnoteref {
    text-indent: 0;
}

.bg-o5 {
    background-image: url(https://scpsandboxcn.wikidot.com/local--files/o9-ppt/o-logo-o5.svg);
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    background-attachment: fixed;
    padding: 0;
}

.inner {
    background-color: rgba(0,0,0,0.835);
    padding: 0.5rem 0.7rem;
    outline: solid 5px rgb(var(--basalt-tertiary-color));
}

.blockquote.u-1, .blockquote.u-2, .blockquote u {
margin:2.25rem auto;
}

.blockquote.u::before, .blockquote.u-1::before, .blockquote.u-2::before {
    display: block;
    width: 2.4rem;
    height: 2.4rem;
    background-color: transparent;
    position: absolute;
    content: '';
    background-position: center;
    background-size: cover;
    background-origin: border-box;
    outline: solid 2px #333;
    transform: translate(-2rem, -1rem);
}

.u p, .u-1 p, .u-2 p {
    margin-left: 1.2rem;
}

.blockquote.u-1::before {
    background-image: url(https://scpsandboxcn.wikidot.com/local--files/oxygen9-files/aic.png);
}

.blockquote.u-2::before {
    background-image: url(https://scpsandboxcn.wikidot.com/local--files/oxygen9-files/user.png);
}

.pd0 .image-container, .pd0 aligncenter, .pd0 td {
    padding:0;
}

#page-content .creditRate {
    float: none;
    transform: none;
    display: block;
    margin: 0;
    text-align: center;
}

.image-container {
    padding: 0;
}

.imgin td {
    padding: 0;
}

span.name {
    font-size: 135%;
    margin: 0rem 0rem -1rem 0rem;
    padding: 0rem 0rem 0rem 1rem;
    display: block;
    width: auto;
}

span.name::after {
    content: '';
    display: block;
    background-color: rgb(var(--basalt-tertiary-color));
    width: 100%;
    position: relative;
    height: 0.125rem;
    top: -0.8rem;
    z-index: 1;
}

.name strong {
    display: block;
    padding-right: 1rem;
    word-break: keep-all;
    background: #000000;
    z-index: 2;
    width: fit-content;
    position: relative;
}

.title h1 {
    background: rgb(var(--basalt-overtone));
    padding: 0.3rem 1.4rem;
    box-shadow: 3rem 0rem rgb(var(--basalt-overtone));
    transform: translateX(-3rem);
    border-left: 1.5rem double #000;
}

.log blockquote {
    background: transparent;
    border: none;
    width: 75%;
    box-shadow: none;
    color: rgb(var(--basalt-alternate-color));
}

.log p {
    padding: 0 2rem;
}

.imgin {
    width: 75vw;
    margin: 0 auto;
}


[[/module]]

[!-- 正文 --]



[[=image https://scpsandboxcn.wikidot.com/local--files/oxygen9-files/cohafmh8.png]]

