/* basic Query for mobile devices */
@media (max-width: 767px) {
    .owindow {
        min-width: 80%;
        max-width: 99%;
    }
    .modal-body .table, .modal-body .table ~ div {
        float: left;
    }
    .owindow .button-bar {
        float: right;
    }
    .owindow div a.btn-primary {
        width: 100%;
        float: left;
    }
    .mobile-top-bar ul li:last-of-type ul {
        right: 0;
    }
    span, a, #action-area td {
        word-break: break-all;
    }
}
/* Mobile Media Query */
@media (max-width: 479px) {
    #search-top-box-input {
        width: 5em;
    }
    #page-content {
        font-size: 0.9em;
    }
    #main-content {
        margin: 0;
    }
    #recent-posts-category {
        width: 100%;
    }
    #header {
        max-width: 90%;
    }
    #side-bar {
        width: 80%;
        position: relative;
    }
    .top-bar {
        display: none;
    }
    .mobile-top-bar {
        display: block;
        padding: 0;
    }
    .page-options-bottom a {
        padding: 0 4px;
    }
    #header h1 a {
        font-size: 100%;
    }
    blockquote, div.blockquote, div.curved {
        margin: 1em 0;
    }
    .license-area {
        font-size: 0.8em;
    }
    #header {
        background-position: 0 5.5em;
        background-size: 55px 55px;
    }
    #header h1, #header h2 {
        margin-left: 66px;
    }
    table.form td, table.form th {
        float: left;
    }
    td.name {
        width: 15em;
    }
    table.form td, table.form th {
        padding: 0;
    }
    #edit-page-title {
        max-width: 90%;
    }
    .content-panel.left-column, .content-panel.right-column {
        width: 99%;
        float: left;
    }
    #page-content div, #page-content div table {
        clear: both;
    }
    #page-content div.title {
        word-break: keep-all;
    }
}
/* Note Media Query */
@media (min-width: 480px) and (max-width: 580px) {
    #search-top-box-input {
        width: 7em;
    }
    #main-content {
        margin: 0 2em 0 2em;
    }
    #header {
        max-width: 90%;
