    #main-content {
        margin: 0 4em 0 20em;
    }
    #header, #top-bar, #side-bar {
        max-width: 100%;
    }
    .top-bar li {
        margin: 0;
    }
    #top-bar ul li.sfhover ul li a, #top-bar ul li:hover ul li a {
        width: 110px;
    }
    .page-options-bottom a {
        padding: 0 7px;
    }
    #header h1 a {
        font-size: 160%;
