      break;
      case 3:document.getElementById("outputStar").innerHTML = "<span style='color: #56b4cc;'>â˜…â˜…â˜…â˜†â˜†</span>";
      break;
      case 4:document.getElementById("outputStar").innerHTML = "<span style='color: #8f54a6;'>â˜…â˜…â˜…â˜…â˜†</span>";
      break;
      case 5:document.getElementById("outputStar").innerHTML = "<span style='color: #f8b60d;'>â˜…â˜…â˜…â˜…â˜…</span>";
      break;
    }
    document.getElementById("outputText").scrollTop = 0;
    document.getElementById("outputText").scrollLeft = 0;
    document.getElementById("outputResult").id = "";
    outputDiv[n].id = "outputResult";
  }
}
