[[module CSS]]
#header {
    background: url(http://scp-wiki-cn.wikidot.com/local--files/site-cn-06/css_logo) 1px 20px no-repeat;
}
#header h1 a span {
    display: none;
}
#header h1 a::before {
    content: "Site-CN-06";
    font-family: 'Times New Roman', cursive;
    color: #ffffff;
    bottom: -2em;
    text-shadow:
        1px 1px 1px #000;
}
#header h2 {
    display: inline-block;
    position: absolute;
    bottom: 30%;
    left: 0;
    right: 0;
    margin: 0 auto;
    padding: 65px 125px 0px;
}
#header h2 span{
    font-size: 0px;
    padding: 0px 0px;
}
#header h2:after {
    content: "创 新 者 前 行";
    color: #ffffff;
    text-shadow: 1px 1px 1px #000;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, .8);
    white-space: pre;
}
div#container-wrap{background: url("http://scp-wiki-cn.wikidot.com/local--files/site-cn-06/css_background") top left repeat-x;
background-position-y: -50px;
}
h1, #page-title {
    color: #282e48;
    padding: 0 0 0.25em;
    margin: 0 0 0.6em;
    font-weight: normal;
}
[[/module]]

