[[module CSS]]
div#page-content div.paras {
    image-rendering: crisp-edges;
    image-rendering: pixelated;
}

/* Project GYAKUTEN Theme */
/* [2022 Wikidot theme] */
/* By Dr Hormress */

div#container-wrap,
div#header {
    background-image: none;
}

div#container-wrap {
    background-image: linear-gradient(to bottom, #2B00FF 0,#9788FC 158px, #9080F0 158px, #9080F0 160px, #9788FC 160px, #9788FC 180px, #9080F0 180px, #9080F0 182px, #9788FC 182px, #9788FC 190px, #AAA 190px, #FFF 195px);
    background-repeat: no-repeat;
    background-size: 100% 200px;
    background-position: center top;
}

div#header {
    height: 160px;
}

div#header h1 span,
div#header h2 {
    display: none;
}

div#header h1 {
    margin: 0;
    padding: 0;
    float: none;
    display: block;
    position: absolute;
    height: 140px;
    width: 100%;
    top: 0;
    left: 0;
}

div#header h1 a {
    margin: 0 auto;
    padding: 0;
    float: none;
    display: block;
    height: 160px;
    max-height: unset;
    width: 320px;
    background-image: url("http://ah-sandbox.wikidot.com/local--files/scp-cn-x143-0/Gyakuten_logo.svg");
    background-repeat: no-repeat;
    background-size: auto 100%;
    background-position: center center;
}

div#page-title {
    display: none;
}

div#top-bar {
    top: 160px;
}

/* 搜索框，部分引自Lt Flops的居中页眉Sigma-9 */

@media (min-width: 581px) and (max-width: 767px) {
    #search-top-box-input {
        width: 8em;
    }
}

@media (min-width: 480px) and (max-width: 580px) {
    #search-top-box-input {
        width: 7em;
    }
}

@media (max-width: 479px) {
    #search-top-box-input {
        display: none;
    }
}

#login-status {
    top: 0.5rem;
    left: 0;
    right: initial;
    z-index: 1;
}

#search-top-box {
    top: 0.5rem;
    right: 0;
}

#search-top-box-input,
#search-top-box-input:hover,
#search-top-box-input:focus,
#search-top-box-form input[type=submit],
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border-radius: 4px;
    box-shadow: none;
    background-image: none;
    background-color: #2B00FF;
    font-size: unset;
}

#search-top-box-input {
    border-color: #9788FC;
}

#search-top-box-input,
#search-top-box-input:hover,
#search-top-box-input:focus {
    box-shadow: inset 0 0 1px #000;
}

#search-top-box-form input[type=submit] {
    border-color: #9080F0;
}

#search-top-box-form input[type=submit],
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    box-shadow: 0 0 1px #000;
}

#side-bar .side-block {
    background-color: #FFF !important;
    border-color: #000;
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.25);
}

#side-bar .side-block:not(.media) img {
    filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
}

#side-bar .side-block hr {
    margin-left: 0;
    margin-right: 0;
    background-color: #000;
}

#side-bar .collapsible-block-folded {
    background-image: none;
}

#side-bar .heading,
#side-bar .collapsible-block-unfolded-link,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #000;
    border-color: #000;
    font-size: 14px;
    font-weight: normal;
}

#side-bar a, #side-bar a:visited, #side-bar a.newpage {
    color: #2B00AA;
}

@media (max-width: 767px) {
#side-bar, #side-bar:target {
    background-color: #FFF;
    background-image: none;
    border: none;
}

#side-bar .close-menu {
    display: block;
    opacity: 0;
    transition: ease 1s;
    -webkit-transition: ease 1s;
}

#side-bar:target .close-menu {
    opacity: 0.5;
    left: 19em;
    width: calc(100% - 19em);
}
}

#top-bar ul li:hover a,
#top-bar ul li.sfhover a {
    color: #2B00AA;
    background-image: none;
}

#top-bar .mobile-top-bar .open-menu a {
    border: solid 1px #EEE;
    border-radius: 4px;
    background-image: none;
    background-color: #2B00FF;
    color: #EEE;
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.25);
}

#top-bar .mobile-top-bar .open-menu a:hover {
    color: #FFF;
    border-color: #FFF;
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.6);
}

.page-rate-widget-box, #page-content .creditRate .rate-box-with-credit-button .page-rate-widget-box {
    background-color: #2B00FF;
    background-image: linear-gradient(to bottom, #2B00AA, #2B00FF);
    border: solid 1px #9080F0;
    border-radius: 4px;
    box-shadow: 0 0 5px #AAA;
    margin-left: 0;
    margin-right: 0;
}

.page-rate-widget-box .rate-points {
    color: #FFF !important;
    background-color: transparent !important;
    border-radius: 0;
    border: none;
}

.page-rate-widget-box .rateup.btn,
.page-rate-widget-box .ratedown.btn,
.page-rate-widget-box .cancel {
    border: none;
    background-color: transparent;
    border-radius: 0;
}

.page-rate-widget-box .rateup.btn a,
.page-rate-widget-box .ratedown.btn a,
.page-rate-widget-box .cancel a {
    border: none;
    background-color: transparent;
    border-radius: 0;
    color: #AAA;
}

.page-rate-widget-box .rateup.btn a:hover,
.page-rate-widget-box .ratedown.btn a:hover,
.page-rate-widget-box .cancel a:hover {
    border: none;
    background-color: transparent;
    border-radius: 0;
    color: #FFF;
}

#page-content .rate-box-with-credit-button {
    display: flex;
    border-radius: 0;
    border: none;
    background-color: transparent;
    box-shadow: none;
}

#page-content .creditRate {
    margin-left: 0;
    margin-right: 0;
}

#page-content .rate-box-with-credit-button .creditButton p a {
    display: inline-block;
    margin: 0;
    margin-left: 5px;
    padding-top: 3px;
    padding-bottom: 2px;
    width: 20px;
    height: 100%;
    font-size: 12px;
    text-align: center;
    color: #AAA;
    background-color: #2B00FF;
    background-image: linear-gradient(to bottom, #2B00AA, #2B00FF);
    border: solid 1px #9080F0;
    border-radius: 4px;
    box-shadow: 0 0 5px #AAA;
    box-sizing: border-box;
    position: static;
}

#page-content .rate-box-with-credit-button .creditButton p a:hover {
    color: #FFF;
}

/* div.modalbox {
    color: #FFF;
    background: linear-gradient(#222 51px, #000 51px, #000);
} */

div.blockquote {
    margin-left: 0;
    margin-right: 0;
    border: solid 1px #2B00AA;
    border-radius: 5px;
    background-image: none;
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.25);
}

/* -------------------- */

div#page-title,
div.meta-title,
h1 {
    color: #000;
    text-align: center;
}

div.meta-title h1 {
    margin: 0;
    color: #2B00AA;
    font-size: 3rem;
    font-weight: 900;
    font-family: "Noto Sans SC", sans-serif;
    text-shadow: 0 0 5px #AAA;
}

div.meta-title h2 {
    margin: 0;
    color: #000;
    font-size: 1rem;
    font-weight: 700;
    font-family: "Noto Sans SC", sans-serif;
}

div#page-content h3 {
    display: flex;
    align-items: center;
}

div#page-content h3::before,
div#page-content h3::after {
    content: "";
    display: block;
    background-color: #666;
    height: 2px;
    flex-grow: 1;
}

div#page-content h3::before {
    margin-right: 0.5rem;
}

div#page-content h3::after {
    margin-left: 0.5rem;
}

div#page-content h4 {
    color: #000;
    font-size: 2rem;
    margin: 0;
}

span.printuser.avatarhover a[href="http://www.wikidot.com/user:info/dr-hormress"]:nth-child(2) {
    font-size: 0;
}

span.printuser.avatarhover a[href="http://www.wikidot.com/user:info/dr-hormress"]:nth-child(2)::before {
    content: "墨菲·吕登";
    font-size: 0.80rem;
}

@media (max-width: 479px) {
span.printuser.avatarhover a[href="http://www.wikidot.com/user:info/dr-hormress"]:nth-child(2)::before {
    content: "墨菲·吕登";
    font-size: 0.72rem;
}
}

div.paras.grayscale div.character-1 {
    filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
}

div.paras.end {
    background-color: #000;
}

div.colmod-link-top div.foldable-list-container {
    display: block !important;
    border: solid 1px #888880;
    border-radius: 10px;
    padding: 20px;
    margin-bottom: 20px;
    box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.1);
    background-color: #FCFDFB;
}

div.foldable-list-container a {
    display: list-item;
    margin-left: 2rem;
    list-style: square;
}

div.foldable-list-container a::after,
div.foldable-list-container a:hover::after {
    display: inline-block;
    content: "\00A0 - 御剑怜侍奇案之……这回不用基金会！";
    color: #000;
    text-decoration: none !important;
}
[[/module]]

